INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-minsk-2029-metro', 'Минск 2020-2029 — Минское метро', 'Игрок исследует архитектуру, поддерживает чистоту и участвует в сценарии гражданской обороны.', 'side', 2, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "get_photography_permit", "text": "Получить разрешение на съёмку и карту маршрута по центральным станциям", "type": "interact", "target": "minsk_metro_photography_permit", "count": 1, "optional": false}, {"id": "explore_central_stations", "text": "Исследовать станции «Плошча Леніна», «Кастрычніцкая» и «Плошча Перамогі», фиксируя архитектурные элементы", "type": "explore", "target": "minsk_metro_central_stations", "count": 3, "optional": false}, {"id": "inspect_systems", "text": "Провести инспекцию системы вентиляции и уборки, чтобы подтвердить легенду о «самом чистом метро»", "type": "investigate", "target": "minsk_metro_systems_inspection", "count": 1, "optional": false}, {"id": "simulate_civil_defense", "text": "Спуститься на глубокие платформы и симулировать сценарий гражданской обороны", "type": "perform", "target": "minsk_metro_civil_defense_simulation", "count": 1, "optional": false}, {"id": "create_media_package", "text": "Составить медиа-пакет и туристический блог-пост для LiveOps-рекомендаций", "type": "craft", "target": "minsk_metro_media_package", "count": 1, "optional": false}]'::jsonb, '{"experience": 1000, "currency": -1, "items": [], "reputation": {"minsk_cultural": 10}, "flags": ["metro_worker_achievement"]}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-minsk-2029-metro", "title": "Минск 2020-2029 — Минское метро", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "2.0.0", "last_updated": "2025-11-29T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["minsk", "metro", "infrastructure"], "topics": ["urban_transport", "soviet_heritage"], "related_systems": ["narrative-service", "travel-service", "exploration-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/cis/minsk/2020-2029/quest-009-minsk-metro.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Городская линия Минска не показывала уникальность метрополитена как культурного объекта и инженерного укрытия.", "goal": "Провести игрока по станциям-символам и раскрыть двойное назначение метро через интерактивные задания.", "essence": "Игрок исследует архитектуру, поддерживает чистоту и участвует в сценарии гражданской обороны.", "key_points": ["Минское метро славится стерильной чистотой и арт-оформлением станций.", "Станции выполняют роль бомбоубежищ и требуют поддержания инфраструктуры.", "Туристический маршрут связывает советское наследие с современным комфортом."]}, "quest_definition": {"quest_type": "side", "level_min": 2, "level_max": null, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "get_photography_permit", "text": "Получить разрешение на съёмку и карту маршрута по центральным станциям", "type": "interact", "target": "minsk_metro_photography_permit", "count": 1, "optional": false}, {"id": "explore_central_stations", "text": "Исследовать станции «Плошча Леніна», «Кастрычніцкая» и «Плошча Перамогі», фиксируя архитектурные элементы", "type": "explore", "target": "minsk_metro_central_stations", "count": 3, "optional": false}, {"id": "inspect_systems", "text": "Провести инспекцию системы вентиляции и уборки, чтобы подтвердить легенду о «самом чистом метро»", "type": "investigate", "target": "minsk_metro_systems_inspection", "count": 1, "optional": false}, {"id": "simulate_civil_defense", "text": "Спуститься на глубокие платформы и симулировать сценарий гражданской обороны", "type": "perform", "target": "minsk_metro_civil_defense_simulation", "count": 1, "optional": false}, {"id": "create_media_package", "text": "Составить медиа-пакет и туристический блог-пост для LiveOps-рекомендаций", "type": "craft", "target": "minsk_metro_media_package", "count": 1, "optional": false}], "rewards": {"experience": 1000, "currency": -1, "items": [], "reputation": {"minsk_cultural": 10}, "flags": ["metro_worker_achievement"]}, "branches": []}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Квест приглашает игрока проехать по ключевым станциям, задокументировать их оформление и проверить системы безопасности.\\nНеобходимо собрать материалы для туристического гида и отчёта городских служб.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Получить разрешение на съёмку и карту маршрута по центральным станциям.\\n2. Исследовать станции «Плошча Леніна», «Кастрычніцкая» и «Плошча Перамогі», фиксируя архитектурные элементы.\\n3. Провести инспекцию системы вентиляции и уборки, чтобы подтвердить легенду о «самом чистом метро».\\n4. Спуститься на глубокие платформы и симулировать сценарий гражданской обороны.\\n5. Составить медиа-пакет и туристический блог-пост для LiveOps-рекомендаций.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Описывает историю открытия метрополитена в 1984 году, использование мрамора и мозаик, а также легенды о дисциплине\\nобслуживания и двойном назначении станций.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- Опыт: 1 000 XP.\\n- Расходы: -1 жетон.\\n- Атрибут «Культура»: +10.\\n- Ачивка «Метрополитеновец».\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "Туристические маршруты, документирование объектов, проверки систем безопасности и мини-игра по поддержанию чистоты.\\n", "mechanics_links": [], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест связан с линиями метро Москвы и Нью-Йорка, формируя глобальную коллекцию подземной инфраструктуры и дополняя\\nгражданско-оборудованные сценарии.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 872, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "2.0.0", "date": "2025-11-29", "author": "content_writer", "changes": "Добавлена полная структура quest_definition с objectives, rewards и branches. Детализирован контент согласно концепции. Добавлены механики городского транспорта и советского наследия."}, {"version": "1.0.0", "date": "2025-11-13", "author": "concept_team", "changes": "Квест «Минское метро» адаптирован под шаблон Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

