INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-vladivostok-2020-2029-golden-bridge', 'Владивосток 2020-2029 — «Золотой мост»', 'Игрок пересекает Золотой мост, открывает виды на бухту Золотой Рог и знакомится с ключевыми фактами о городе.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-vladivostok-2020-2029-golden-bridge", "title": "Владивосток 2020-2029 — «Золотой мост»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["vladivostok", "quest", "infrastructure"], "topics": ["city_quests", "exploration"], "related_systems": ["narrative-service", "world-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/cis/vladivostok/2020-2029/quest-001-golden-bridge.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Требуется вводный квест Владивостока, показывающий символ города и знакомящий игроков с панорамами бухты.", "goal": "Создать лёгкое задание на посещение Золотого моста с акцентом на архитектурные и туристические мотивы.", "essence": "Игрок пересекает Золотой мост, открывает виды на бухту Золотой Рог и знакомится с ключевыми фактами о городе.", "key_points": ["Мост связывает центр Владивостока и задаёт визуальный символ региона.", "Квест использует туристическую и архитектурную механику для мягкого онбординга.", "Подсвечены детали строительства к саммиту АТЭС-2012 и ночная подсветка."]}, "content": {"sections": [{"id": "setup", "title": "Контекст", "body": "Квест открывает серию Владивостока. Игрок получает предложение посетить Золотой мост,\\nчтобы познакомиться с городом и оценить панорамы бухты Золотой Рог.\\n", "mechanics_links": [], "assets": []}, {"id": "phases", "title": "Этапы прохождения", "body": "1. Добраться до въезда на мост и осмотреть золотые опоры.\\n2. Пересечь мост пешком или на транспорте, наблюдая бухту.\\n3. Сделать фотографии панорамы и отметить ключевые точки обзора.\\n4. Вернуться ночью, чтобы увидеть подсветку конструкции.\\n", "mechanics_links": [], "assets": []}, {"id": "details", "title": "Факты и отсылки", "body": "- Вантовый мост длиной 1,388 метра построен к саммиту АТЭС-2012.\\n- Сравнение бухты с аналогами из других мегаполисов подчёркивает глобальный масштаб.\\n- Визуальные детали: золотистые тросы, отражение в воде, архитектурная элегантность.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "Игрок получает 800 XP, эстетический бонус (+10) и ачивку «Покоритель Золотого Моста».\\nВалюта не начисляется, что подчёркивает туристическую природу задания.\\n", "mechanics_links": [], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест открывает архитектурную ветку Владивостока и подготавливает к заданиям о Русском мосте и острове Русский.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-12", "author": "concept_team", "changes": "Конвертация квеста «Золотой мост» в формат knowledge-entry."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

