INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-yerevan-lavash-baking', 'Ереван 2020-2029 — «Выпечка лаваша»', 'Игрок присоединяется к армянской семье, готовит лаваш и получает гастрономический бафф.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-yerevan-lavash-baking", "title": "Ереван 2020-2029 — «Выпечка лаваша»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["yerevan", "lavash", "culinary"], "topics": ["intangible-heritage", "family-tradition"], "related_systems": ["narrative-service", "mood-system", "economy-system"], "related_documents": [{"id": "canon-region-cis-armenia-2020-2093", "relation": "contextualizes"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/cis/yerevan/2020-2029/quest-007-lavash-baking.md", "visibility": "internal", "audience": ["narrative", "quest-design", "economy"], "risk_level": "low"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Markdown не описывал процесс приготовления лаваша и его значение как нематериального наследия ЮНЕСКО.", "goal": "Структурировать мастер-класс по выпечке в тоннире и показать семейные традиции.", "essence": "Игрок присоединяется к армянской семье, готовит лаваш и получает гастрономический бафф.", "key_points": ["Тоннир как центральный объект кухни и символ гостеприимства.", "Совместный труд нескольких поколений усиливает эмоциональную линию.", "Квест поддерживает гастрономическую экономику и культурное наследие Армении."]}, "content": {"sections": [{"id": "preparation", "title": "Подготовка теста", "body": "Игрок замешивает тонкое тесто, слушая советы бабушки и дочери.\\nПроверка ловкости влияет на качество заготовок.\\n", "mechanics_links": [], "assets": []}, {"id": "baking", "title": "Выпечка в тоннире", "body": "Команда приклеивает пласты к стенкам печи, контролирует время и цвет.\\nНад печью активируется mood-эффект «Тёплый дом».\\n", "mechanics_links": [], "assets": []}, {"id": "sharing", "title": "Сервировка и история", "body": "Семья рассказывает о роли лаваша на праздниках и свадьбах.\\nИгрок решает, записать ли рецепт в кулинарный альманах.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- 700 XP, −10 кредов, бафф «Lavash Warmth» (+10% Mood, +10% Stamina на 6 часов).\\n- Ачивка «Лаваш мастер» и +5 репутации гастрономической линии.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [{"term": "Тоннир", "definition": "Традиционная армянская печь-колодец, используемая для выпечки лаваша."}], "references": [], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-12", "author": "concept_director", "changes": "Конвертация квеста «Выпечка лаваша» в knowledge-entry и описание семейного мастер-класса."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

