INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-minsk-2029-belovezhskaya-pushcha', 'Минск 2020-2029 — Беловежская пуща', 'Игрок исследует древний лес, проводит сафари на зубров и участвует в VR-реконструкции подписания соглашений.', 'main', 8, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "organize_trip", "text": "Организовать поездку из Минска и получить разрешение на сафари в заповеднике", "type": "travel", "target": "minsk_belovezhskaya_pushcha_trip", "count": 1, "optional": false}, {"id": "observe_bisons", "text": "Провести наблюдение за зубрами и задокументировать их поведение для базы данных", "type": "investigate", "target": "minsk_belovezhskaya_pushcha_bisons", "count": 1, "optional": false}, {"id": "visit_ded_moroz_residence", "text": "Посетить резиденцию Деда Мороза и собрать материалы о туристической инфраструктуре", "type": "explore", "target": "minsk_belovezhskaya_pushcha_ded_moroz", "count": 1, "optional": false}, {"id": "digitize_agreement_room", "text": "Обнаружить и оцифровать комнату подписания Беловежских соглашений через VR-модуль", "type": "research", "target": "minsk_belovezhskaya_pushcha_agreement_room", "count": 1, "optional": false}, {"id": "prepare_liveops_report", "text": "Подготовить отчёт для LiveOps с рекомендациями по сезонным экспедициям", "type": "craft", "target": "minsk_belovezhskaya_pushcha_report", "count": 1, "optional": false}]'::jsonb, '{"experience": 5000, "currency": 0, "items": [], "reputation": {"minsk_nature": 20}, "flags": ["belovezhskaya_pushcha_explorer_title"]}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-minsk-2029-belovezhskaya-pushcha", "title": "Минск 2020-2029 — Беловежская пуща", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "2.0.0", "last_updated": "2025-11-29T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["minsk", "belovezhskaya-pushcha", "nature"], "topics": ["natural_reserves", "historical_events"], "related_systems": ["narrative-service", "travel-service", "exploration-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/cis/minsk/2020-2029/quest-010-belovezhskaya-pushcha.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Природно-историческая линия Беларуси не показывала масштаб Беловежской пущи и её роль в распаде СССР.", "goal": "Организовать экспедицию с акцентом на экосистему, символичных животных и политическое наследие 1991 года.", "essence": "Игрок исследует древний лес, проводит сафари на зубров и участвует в VR-реконструкции подписания соглашений.", "key_points": ["Пуща — объект UNESCO с тысячелетней историей и уникальной фауной.", "События 1991 года делают локацию ключевой для постсоветского лора.", "Экспедиция сочетает природные задания и интерактивное изучение истории."]}, "quest_definition": {"quest_type": "main", "level_min": 8, "level_max": null, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "organize_trip", "text": "Организовать поездку из Минска и получить разрешение на сафари в заповеднике", "type": "travel", "target": "minsk_belovezhskaya_pushcha_trip", "count": 1, "optional": false}, {"id": "observe_bisons", "text": "Провести наблюдение за зубрами и задокументировать их поведение для базы данных", "type": "investigate", "target": "minsk_belovezhskaya_pushcha_bisons", "count": 1, "optional": false}, {"id": "visit_ded_moroz_residence", "text": "Посетить резиденцию Деда Мороза и собрать материалы о туристической инфраструктуре", "type": "explore", "target": "minsk_belovezhskaya_pushcha_ded_moroz", "count": 1, "optional": false}, {"id": "digitize_agreement_room", "text": "Обнаружить и оцифровать комнату подписания Беловежских соглашений через VR-модуль", "type": "research", "target": "minsk_belovezhskaya_pushcha_agreement_room", "count": 1, "optional": false}, {"id": "prepare_liveops_report", "text": "Подготовить отчёт для LiveOps с рекомендациями по сезонным экспедициям", "type": "craft", "target": "minsk_belovezhskaya_pushcha_report", "count": 1, "optional": false}], "rewards": {"experience": 5000, "currency": 0, "items": [], "reputation": {"minsk_nature": 20}, "flags": ["belovezhskaya_pushcha_explorer_title"]}, "branches": []}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Квест запускает многочасовую экспедицию: нужно добраться до заповедника, наладить взаимодействие с егерями и подготовить\\nматериалы для LiveOps-контента о природе и истории.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Организовать поездку из Минска и получить разрешение на сафари.\\n2. Провести наблюдение за зубрами и задокументировать их поведение для базы данных.\\n3. Посетить резиденцию Деда Мороза и собрать материалы о туристической инфраструктуре.\\n4. Обнаружить и оцифровать комнату подписания Беловежских соглашений через VR-модуль.\\n5. Подготовить отчёт для LiveOps с рекомендациями по сезонным экспедициям.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Раздел раскрывает особенности древнего леса, численность зубров, климатические условия и роль пущи в геополитических\\nпеременах 1991 года.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- Опыт: 5 000 XP.\\n- Атрибут «Природа»: +20.\\n- Эмоциональная сцена распада СССР.\\n- Доступ к LiveOps-ивенту «Вечер в Пуще».\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "Природные экспедиции, следопытские мини-игры, VR-реконструкции исторических событий и сбор лора для базы знаний.\\n", "mechanics_links": [], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест соединяет природную и политическую линии Беларуси, открывая доступ к сюжетам о постсоветских трансформациях и\\nинфраструктуре заповедников.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 873, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "2.0.0", "date": "2025-11-29", "author": "content_writer", "changes": "Добавлена полная структура quest_definition с objectives, rewards и branches. Детализирован контент согласно концепции. Добавлены механики природных заповедников и исторических событий."}, {"version": "1.0.0", "date": "2025-11-13", "author": "concept_team", "changes": "Квест «Беловежская пуща» адаптирован под шаблон Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

