INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-yerevan-duduk-music', 'Ереван 2020-2029 — «Музыка дудука»', 'Игрок слушает душевные мелодии, учится играть и получает музыкальный бафф.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-yerevan-duduk-music", "title": "Ереван 2020-2029 — «Музыка дудука»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["yerevan", "duduk", "music"], "topics": ["intangible-heritage", "performance"], "related_systems": ["narrative-service", "mood-system", "skill-system"], "related_documents": [{"id": "canon-region-cis-armenia-2020-2093", "relation": "contextualizes"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/cis/yerevan/2020-2029/quest-008-duduk-music.md", "visibility": "internal", "audience": ["narrative", "quest-design", "audio"], "risk_level": "low"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Markdown не отражал мастер-класс игры на дудуке и влияние на музыкальные навыки.", "goal": "Задокументировать урок у мастера дудука и подчеркнуть статус нематериального наследия ЮНЕСКО.", "essence": "Игрок слушает душевные мелодии, учится играть и получает музыкальный бафф.", "key_points": ["Дудук из абрикосового дерева и его уникальный тембр.", "Диалоги о роли дудука в траурных и свадебных церемониях.", "Квест повышает музыкальный навык и mood-параметр."]}, "content": {"sections": [{"id": "workshop", "title": "Мастер-класс", "body": "Игрок встречает мастера, изучает дыхание и постановку пальцев.\\nПервая мелодия активирует мини-игру на точность.\\n", "mechanics_links": [], "assets": []}, {"id": "performance", "title": "Выступление", "body": "Ученики играют в дуэте с наставником, создавая меланхоличную атмосферу.\\nИгрок решает выступать ли на городском мероприятии.\\n", "mechanics_links": [], "assets": []}, {"id": "heritage", "title": "Нематериальное наследие", "body": "Мастер рассказывает о признании дудука ЮНЕСКО и передаёт историю учеников.\\nЗаписи мелодий добавляются в аудиоархив проекта.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- 900 XP, +15 к навыку «Музыка», бафф «Duduk Soul» (+15% Mood на 8 часов).\\n- Ачивка «Голос Армении» и +5 к репутации культурной линии.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [{"term": "Дудук", "definition": "Традиционный армянский деревянный духовой инструмент с тёплым тембром, признанный ЮНЕСКО."}], "references": [], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-12", "author": "concept_director", "changes": "Конвертация квеста «Музыка дудука» в knowledge-entry и описание мастер-класса."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

