INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-moscow-2030-2039-dome-charter', 'Москва 2030-2039 — «Хартия Купола»', 'Игрок координирует активистов, собирает цифровые подписи и сталкивается с корпоративным советом, чтобы добиться прав для серых слоёв.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-moscow-2030-2039-dome-charter", "title": "Москва 2030-2039 — «Хартия Купола»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-13T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["moscow", "quest", "activism"], "topics": ["social-justice", "arcology-politics"], "related_systems": ["narrative-service", "social-service", "faction-service"], "related_documents": [{"id": "timeline-author-quests-index", "relation": "references"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/cis/moscow/2030-2039/quest-011-dome-charter.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Политический конфликт в серых слоях Архологии был описан упрощённо и не учитывал вариативность хода кампании.", "goal": "Разработать квест о принятии «Хартии Купола» с этапами агитации, переговоров и противодействия саботажу.", "essence": "Игрок координирует активистов, собирает цифровые подписи и сталкивается с корпоративным советом, чтобы добиться прав для серых слоёв.", "key_points": ["Гражданская кампания состоит из митинга, сбора подписей и переговоров с советом.", "Корпоративные противники устраивают саботаж, требующий выбора силовых или дипломатических мер.", "Итог голосования задаёт направление социальной линии Архологии."]}, "content": {"sections": [{"id": "rally", "title": "Подпольный митинг", "body": "Игрок присутствует на собрании активистов, обсуждает стратегию кампании и определяет, какие аргументы использовать против корпорации.\\n", "mechanics_links": [], "assets": []}, {"id": "signatures", "title": "Сбор цифровых подписей", "body": "Мини-игра агитации в серых слоях: нужно собрать 1 000 подписей, общаясь с NPC, преодолевая дезинформацию и обеспечивая защиту данных.\\n", "mechanics_links": [], "assets": []}, {"id": "negotiations", "title": "Переговоры с советом", "body": "Игрок участвует в заседании корпоративного совета, где решается судьба Хартии. Проверки дипломатии и риторики определяют,\\nудастся ли отстоять права жителей.\\n", "mechanics_links": [], "assets": []}, {"id": "sabotage", "title": "Противодействие саботажу", "body": "Корпоративные агенты пытаются сорвать голосование. Игрок выбирает между силовым отражением, информационным ударом или юридической блокировкой.\\n", "mechanics_links": [], "assets": []}, {"id": "outcomes", "title": "Последствия и награды", "body": "- 1 800 XP, 3 000 едди и репутация +5 с серыми слоями или корпорациями в зависимости от решения.\\n- Если Хартия принята — улучшение условий жизни и доступ к линии «Социальное движение».\\n- При провале активисты подвергаются репрессиям, открывая ответные квесты.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 874, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-13", "author": "concept_team", "changes": "Конвертация квеста «Хартия Купола» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, 