INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-yerevan-velvet-revolution', 'Ереван 2020-2029 — «Бархатная революция»', 'Игрок присоединяется к мирным митингам, помогает координации и переживает смену власти.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-yerevan-velvet-revolution", "title": "Ереван 2020-2029 — «Бархатная революция»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["yerevan", "politics", "protest"], "topics": ["civic-activism", "modern-history"], "related_systems": ["narrative-service", "reputation-system", "choice-system"], "related_documents": [{"id": "canon-region-cis-armenia-2020-2093", "relation": "contextualizes"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/cis/yerevan/2020-2029/quest-009-velvet-revolution.md", "visibility": "internal", "audience": ["narrative", "quest-design", "politics"], "risk_level": "medium"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Markdown не описывал геймплей участия в акциях «бархатной революции» 2018 года.", "goal": "Структурировать квест по современным протестам с моральным выбором и влиянием на репутацию.", "essence": "Игрок присоединяется к мирным митингам, помогает координации и переживает смену власти.", "key_points": ["В центре Еревана собираются тысячи граждан с плакатами и песнями.", "Игрок решает, поддерживать ли лидеров или оставаться наблюдателем.", "Квест привязан к политической ветке и навыкам убеждения."]}, "content": {"sections": [{"id": "mobilization", "title": "Мобилизация", "body": "Игрок распространяет сообщения в соцсетях, организует колонну и выбирает слоганы.\\nМини-игра проверяет скорость распространения информации.\\n", "mechanics_links": [], "assets": []}, {"id": "march", "title": "Марш и митинг", "body": "Колонна двигается к площади Республики, участники скандируют лозунги.\\nИгрок решает, вступать ли в переговоры с полицией.\\n", "mechanics_links": [], "assets": []}, {"id": "choice", "title": "Выбор стратегии", "body": "- Ненасильственный протест: повысить репутацию народа, но риск задержек.\\n- Радикализация: ускорить события, но получить штраф к международной поддержке.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- 2 500 XP, +15 к репутации гражданского движения.\\n- Ачивка «Голос улиц» и доступ к политическим квестам Армении.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [{"term": "Бархатная революция (2018)", "definition": "Мирная смена власти в Армении под руководством Никола Пашиняна."}], "references": [], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-12", "author": "concept_director", "changes": "Конвертация квеста «Бархатная революция» в knowledge-entry и описание протестного геймплея."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

