INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-moscow-2039-memory-ark', 'Москва 2030-2039 — Ковчег памяти', 'Игрок ищет старые устройства, восстанавливает их и загружает данные в защищённое хранилище.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-moscow-2039-memory-ark", "title": "Москва 2030-2039 — Ковчег памяти", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-13T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["moscow", "data-preservation", "history"], "topics": ["digital_archives", "cultural_memory"], "related_systems": ["narrative-service", "investigation-service", "lore-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/cis/moscow/2030-2039/quest-012-memory-ark.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "В московской линии отсутствовал квест, показывающий борьбу за сохранение памяти до DataKrash.", "goal": "Помочь ветерану-сетраннеру собрать цифровой архив и открыть доступ к потерянным воспоминаниям.", "essence": "Игрок ищет старые устройства, восстанавливает их и загружает данные в защищённое хранилище.", "key_points": ["Редкие носители содержат эмоции и знания о мире до краха сетей.", "Восстановление данных требует технических проверок и выбора приоритетов.", "Ковчег становится источником лор-контента для будущих сюжетов."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Квест приглашает игрока помочь старому хакеру создать цифровое убежище памяти. Нужно разыскать устройства с утерянными\\nвоспоминаниями, восстановить их и загрузить в защищённый архив.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Получить задание и список предполагаемых хранилищ данных.\\n2. Найти пять старых устройств в разных районах Москвы и извлечь их без повреждений.\\n3. Восстановить данные через проверку навыка «Технологии» и собрать фрагменты истории.\\n4. Загрузить воспоминания в Ковчег и защитить архив от сетевых угроз.\\n5. Посмотреть ключевые воспоминания и открыть новые лор-страницы для игроков.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- Опыт: 900 XP.\\n- Валюта: 1 500 еддис.\\n- Уникальный доступ к историческим воспоминаниям.\\n- Ачивка «Хранитель прошлого».\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "Коллекционирование артефактов, технические проверки, сторителлинг через катсцены и расширение базы лора.\\n", "mechanics_links": [], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест открывает лор-базу данных «Pre-DataKrash World» и связывает московскую линию с глобальными сюжетами восстановления\\nпамяти.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 875, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-13", "author": "concept_team", "changes": "Квест «Ковчег памяти» адаптирован под шаблон Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.reward