INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-moscow-2030-2039-liquidator-tender', 'Москва 2030-2039 — «Тендер ликвидаторов»', 'Игрок регистрирует команду ликвидаторов, защищает зону, соревнуется с соперниками и выводит артефакты из опасной территории.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-moscow-2030-2039-liquidator-tender", "title": "Москва 2030-2039 — «Тендер ликвидаторов»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-13T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["moscow", "quest", "mercenary"], "topics": ["pvpve", "hazardous-operations"], "related_systems": ["narrative-service", "combat-service", "liveops-events-service"], "related_documents": [{"id": "timeline-author-quests-index", "relation": "references"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/cis/moscow/2030-2039/quest-013-liquidator-tender.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "high"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Описание тендера на зачистку зоны не содержало структуры испытаний и влияния рейтинга.", "goal": "Создать PvPvE событие с волнами противников, конкуренцией команд и системой наград по итоговому рейтингу.", "essence": "Игрок регистрирует команду ликвидаторов, защищает зону, соревнуется с соперниками и выводит артефакты из опасной территории.", "key_points": ["Зона наполнена мутантами и роуг-техникой, волны нападений идут до финальной экстракции.", "Команды конкурируют за очки, возможны кооперация или саботаж.", "Рейтинговая таблица определяет награды и репутацию."]}, "content": {"sections": [{"id": "registration", "title": "Регистрация и брифинг", "body": "Команда проходит регистрацию, получает экипировку и слушает брифинг о целях, конкурентах и зонах зачистки.\\n", "mechanics_links": [], "assets": []}, {"id": "waves", "title": "Волны зачистки", "body": "Игрок отражает несколько волн мутантов и роуг-техники, используя кооперативные и тактические механики. За уничтожение угроз начисляются очки.\\n", "mechanics_links": [], "assets": []}, {"id": "competition", "title": "Конкуренция команд", "body": "Соперники пытаются опередить игрока. Можно объединиться, конкурировать честно или саботировать чужую технику, что повлияет на репутацию.\\n", "mechanics_links": [], "assets": []}, {"id": "extraction", "title": "Экстракция артефактов", "body": "Завершающий этап — эвакуация с добытыми артефактами. Контроль времени и ресурсов определяет итоговый рейтинг.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "- 2 000 XP, 5 000–15 000 едди, редкие образцы оружия.\\n- Рейтинг влияет на дальнейшие контракты и доступ к еженедельным событиям.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 876, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-13", "author": "concept_team", "changes": "Конвертация квеста «Тендер ликвидаторов» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content