INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-moscow-2030-2039-humanity-ordinance', 'Москва 2030-2039 — «Ордонанс человечности»', 'Игрок расследует принятие закона, собирает компромат и решает, публиковать ли его, шантажировать корпорацию или продать конкурентам.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-moscow-2030-2039-humanity-ordinance", "title": "Москва 2030-2039 — «Ордонанс человечности»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-13T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["moscow", "quest", "legislation"], "topics": ["cyber-regulation", "investigative-journalism"], "related_systems": ["narrative-service", "social-service", "faction-service"], "related_documents": [{"id": "timeline-author-quests-index", "relation": "references"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/cis/moscow/2030-2039/quest-014-humanity-ordinance.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "История с «Ордонансом человечности» не имела структурированного расследования и разветвления по решениям.", "goal": "Создать журналистский квест о корпоративном лоббировании закона, ограничивающего модификации тела.", "essence": "Игрок расследует принятие закона, собирает компромат и решает, публиковать ли его, шантажировать корпорацию или продать конкурентам.", "key_points": ["Анонс закона вызывает протесты и поиск бенефициаров.", "Сбор доказательств раскрывает махинации корпорации на рынке имплантов.", "Решение игрока меняет доступность модификаций и отношение фракций."]}, "content": {"sections": [{"id": "announcement", "title": "Анонс закона", "body": "Игрок получает информацию об «Ордонансе человечности 2.0», наблюдает реакцию общества и собирает первые leads для расследования.\\n", "mechanics_links": [], "assets": []}, {"id": "investigation", "title": "Расследование", "body": "Продолжение включает опрос источников, взлом корпоративных архивов и проверку причастности лоббистов (проверки Расследование DC 18).\\n", "mechanics_links": [], "assets": []}, {"id": "evidence", "title": "Сбор доказательств", "body": "Игрок ищет подтверждения в виде документов и записей переговоров, формируя пакет компромата для дальнейших решений.\\n", "mechanics_links": [], "assets": []}, {"id": "resolution", "title": "Финальное решение", "body": "Три развилки: опубликовать материалы и сорвать закон, шантажировать корпорацию ради прибыли или продать данные конкурентам.\\nКаждая ветка меняет экономику имплантов и репутацию.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "- 3 500 XP и 0–50 000 едди в зависимости от выбора.\\n- Репутационные изменения: народ/антикорпорации, корпорации или конкуренты.\\n- Долгосрочные изменения рынка имплантов и новые сюжетные ветки.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 877, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-13", "author": "concept_team", "changes": "Конвертация квеста «Ордонанс человечности» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

