INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-tbilisi-georgian-dance', 'Тбилиси 2020-2029 — «Грузинский танец»', 'Игрок посещает шоу ансамбля, затем проходит интенсив по движениям и выступает перед зрителями супры.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-tbilisi-georgian-dance", "title": "Тбилиси 2020-2029 — «Грузинский танец»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["tbilisi", "dance", "culture"], "topics": ["performance", "training"], "related_systems": ["narrative-service", "culture-system", "talent-system"], "related_documents": [{"id": "canon-region-cis-tbilisi-2020-2029", "relation": "contextualizes"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/cis/tbilisi/2020-2029/quest-008-georgian-dance.md", "visibility": "internal", "audience": ["narrative", "quest-design", "animation"], "risk_level": "medium"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Markdown-квест не раскрывал этапы обучения грузинскому танцу и различия между лезгинкой и картули.", "goal": "Описать сочетание зрительского опыта и тренировки с мастерством стоять на носках.", "essence": "Игрок посещает шоу ансамбля, затем проходит интенсив по движениям и выступает перед зрителями супры.", "key_points": ["Вовлечены легендарные школы (например, «Сухишвили»).", "Тренировка включает баланс на носках ног и работу с кинжалами.", "Финальный выход на сцену влияет на репутацию танцора."]}, "content": {"sections": [{"id": "performance", "title": "Зрительская часть", "body": "Игрок смотрит шоу грузинского ансамбля, отмечая элементы лезгинки и картули.\\nAR-комментарии объясняют символику движений.\\n", "mechanics_links": [], "assets": []}, {"id": "training", "title": "Тренировка", "body": "- Уроки баланса: стойка на носках, движения рук.\\n- Работа с кинжалами и ритмическая мини-игра.\\n", "mechanics_links": [], "assets": []}, {"id": "rehearsal", "title": "Репетиция", "body": "Игрок тренируется в паре, отрабатывая синхронизацию и мимику.\\n", "mechanics_links": [], "assets": []}, {"id": "finale", "title": "Выступление", "body": "Финальный номер проходит на супре; успех зависит от точности попадания в ритм.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- 1 500 XP.\\n- Издержки: −40 кредов.\\n- +15 к навыку «Танцы».\\n- Ачивка «Танцор Кавказа».\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [{"term": "Лезгинка", "definition": "Мужской кавказский танец с акцентом на движение на носках и энергичную пластику."}], "references": [], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-12", "author": "concept_director", "changes": "Конвертация квеста «Грузинский танец» в knowledge-entry."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

