-- Quest: quest-moscow-2039-family-ties
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-moscow-2039-family-ties', 'Москва 2030-2039 — Семейные узы', 'Игрок знакомится через приложения, развивает отношения, проводит свадьбу и управляет семейной экономикой.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-moscow-2039-family-ties", "title": "Москва 2030-2039 — Семейные узы", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-13T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["moscow", "relationships", "family"], "topics": ["social_simulation", "longform_progression"], "related_systems": ["narrative-service", "housing-service", "wellbeing-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/cis/moscow/2030-2039/quest-015-family-ties.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Линия Москвы требовала долгосрочного социального контента про создание семьи в техногенном мегаполисе.", "goal": "Построить путь от знакомств до совместной жизни и воспитания детей.", "essence": "Игрок знакомится через приложения, развивает отношения, проводит свадьбу и управляет семейной экономикой.", "key_points": ["Квест раскрывает контраст между киберпанк-средой и традиционными ценностями.", "Семейные решения дают ощутимые игровые бонусы и риски.", "Линия открывает серию семейных событий и LiveOps праздников."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Квест запускает кампанию «Семейные узы»: игроку предлагается построить отношения, организовать свадьбу и создать совместный\\nдом, несмотря на давление корпораций и гильдий.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Настроить профиль в датинг-сервисе или познакомиться в офлайн-локации.\\n2. Провести серию свиданий с управлением ресурсов и эмоциональных статов.\\n3. Сделать предложение, подготовить кольцо и выбрать формат свадьбы.\\n4. Провести церемонию с приглашением друзей и фиксацией репутации.\\n5. Настроить совместный бюджет, жилище и распределение обязанностей.\\n6. Завести детей естественным путём, через биоинженерию или усыновление, открывая семейные под-квесты.\\n", "mechanics_links": [], "assets": []}, {"id": "choices", "title": "Ключевые выборы", "body": "Традиционная свадьба увеличивает затраты, но повышает репутацию. Быстрая регистрация снижает расходы. Отказ от официального\\nбрака оставляет меньше бонусов, но минимизирует риски. Возможен развод с потерей средств и репутации.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- Опыт: 2 000 XP.\\n- Расходы: до 5 000 Eddies на подготовку свадьбы.\\n- Семейные бонусы: +10% XP от сюжетных квестов, точка респавна «дом», доступ к семейным событиям.\\n", "mechanics_links": [], "assets": []}, {"id": "risks", "title": "Риски", "body": "Партнёр может погибнуть в событиях, что требует спасательных операций или приводит к статусу вдовца. Развод снижает бюджет и\\nрепутацию. Появление детей создаёт новые обязательства и точки атаки врагов.\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "Симуляция отношений, управление совместным инвентарём, семейные события, NPC-дети с прогрессией, привязка LiveOps-праздников.\\n", "mechanics_links": [], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест открывает семейные сюжетные линии Москвы и связывается с социальными сервисами, влияя на репутацию среди гильдий и корпораций.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 878, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-13", "author": "concept_team", "changes": "Квест «Семейные узы» адаптирован под шаблон Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

