INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-vladivostok-2020-2029-submarine-c-56', 'Владивосток 2020-2029 — «Подводная лодка С-56»', 'Игрок исследует внутренние отсеки подлодки, изучает боевые эпизоды и отдает честь экипажу.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-vladivostok-2020-2029-submarine-c-56", "title": "Владивосток 2020-2029 — «Подводная лодка С-56»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["vladivostok", "quest", "naval_history"], "topics": ["city_quests", "heritage"], "related_systems": ["narrative-service", "museum-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/cis/vladivostok/2020-2029/quest-007-submarine-c-56.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Необходимо показать героическое прошлое Тихоокеанского флота и связать игроков с мемориалом WWII.", "goal": "Организовать интерактивное посещение субмарины С-56 с фокусом на историческом опыте и музейных механиках.", "essence": "Игрок исследует внутренние отсеки подлодки, изучает боевые эпизоды и отдает честь экипажу.", "key_points": ["С-56 — знаменитая субмарина, потопившая десять кораблей во время WWII.", "Маршрут внутри корабля раскрывает жизнь моряков в ограниченных условиях.", "Квест поддерживает образовательные и мемориальные активности города."]}, "content": {"sections": [{"id": "entrance", "title": "Вход и подготовка", "body": "Игрок прибывает к мемориалу, проходит инструктаж и знакомится с историей субмарины через AR-экспозицию.\\nНа этом этапе активируются образовательные подсказки и голосовые комментарии ветеранов.\\n", "mechanics_links": [], "assets": []}, {"id": "deck_tour", "title": "Осмотр отсеков", "body": "Внутри необходимо пройти торпедный отсек, каюты, рубку и машинное отделение.\\nКаждый модуль содержит интерактивные элементы: запуск торпеды в тренировочном режиме, настройка приборов связи.\\n", "mechanics_links": [], "assets": []}, {"id": "reflection", "title": "Память экипажа", "body": "Завершая экскурсию, игрок собирает цифровые записи из дневников моряков и оставляет сообщение в книге памяти.\\nОпционально можно активировать мемориальную церемонию с салютом Тихоокеанского флота.\\n", "mechanics_links": [], "assets": []}, {"id": "outreach", "title": "Связь с сообществом", "body": "Полученные материалы отправляются в музейную сеть города, открывая дополнительные задания для школьных туров\\nи совместных мероприятий с Военно-морской академией.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-12", "author": "concept_team", "changes": "Конвертация квеста «Подводная лодка С-56» в формат knowledge-entry."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

