INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-moscow-2039-black-market-raid', 'Москва 2030-2039 — Рейд на чёрный рынок', 'Контракт ведёт к атаке на архологический рынок, но игрок может предупредить торговцев и изменить баланс сил.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-moscow-2039-black-market-raid", "title": "Москва 2030-2039 — Рейд на чёрный рынок", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-13T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["moscow", "black-market", "raid"], "topics": ["moral_choice", "faction_dynamics"], "related_systems": ["narrative-service", "combat-service", "economy-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/cis/moscow/2030-2039/quest-016-black-market-raid.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "high"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Линия Москвы нуждалась в фракционном выборе между корпорациями и подпольными торговцами.", "goal": "Дать игроку альтернативы — сорвать рынок или помочь ему уйти в подполье.", "essence": "Контракт ведёт к атаке на архологический рынок, но игрок может предупредить торговцев и изменить баланс сил.", "key_points": ["Выбор влияет на экономические бонусы и доступ к нелегальным товарам.", "Рейд предлагает интенсивный бой с поддержкой корпорации.", "Предупреждение открывает скидки и доверие подпольных сетей."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Корпоративный агент поручает уничтожить чёрный рынок в архологии. Игрок проходит разведку, после чего решает, атаковать ли\\nторговцев или предупредить их об угрозе.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Подписать контракт с корпорацией и получить координаты рынка.\\n2. Провести разведку, собрать доказательства и выявить охрану.\\n3. Выбрать между рейдом или эвакуацией.\\n4. Завершить миссию, собрав трофеи или помогая торговцам скрыться.\\n", "mechanics_links": [], "assets": []}, {"id": "branch_a", "title": "Ветка «Рейд»", "body": "Масштабный бой против более десяти противников, захват складов, уничтожение логистики рынка, получение выплаты и роста\\nрепутации корпорации.\\n", "mechanics_links": [], "assets": []}, {"id": "branch_b", "title": "Ветка «Предупреждение»", "body": "Информирование торговцев, организация эвакуации товаров, защита от рейдеров и последующее партнерство с подпольем.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- Ветка рейда - 1 500 XP, 10 000 Eddies, репутация корпорации +5, репутация рынка -10.\\n- Ветка предупреждения - 1 200 XP, 5 000 Eddies, репутация рынка +8, скидка 40% на чёрном рынке.\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "Фракционный выбор, боевая миссия, сопровождение NPC, влияние на экономику региональных магазинов, доступ к эксклюзивным\\nтоварам через LiveOps.\\n", "mechanics_links": [], "assets": []}, {"id": "connections", "title": "Связи", "body": "Результаты квеста влияют на последующие поставки нелегальных товаров и отношение корпораций к игроку.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 879, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-13", "author": "concept_team", "changes": "Квест «Рейд на чёрный рынок» адаптирован под 