-- Quest: canon-quest-amsterdam-2020-2029-stroopwafel-quest
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-amsterdam-2020-2029-stroopwafel-quest', 'Амстердам 2020-2029 — Квест строопвафлей', 'Игрок пробует свежую карамельную вафлю на рынке Albert Cuypmarkt и берёт сладкий сувенир домой.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-amsterdam-2020-2029-stroopwafel-quest", "title": "Амстердам 2020-2029 — Квест строопвафлей", "document_type": "canon", "category": "quest", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-12T02:30:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "lore_analyst", "contact": "lore@necp.game"}], "tags": ["europe", "amsterdam", "quest"], "topics": ["timeline-author", "street-food"], "related_systems": ["narrative-service", "economy-service"], "related_documents": [{"id": "canon-region-europe-amsterdam-2020-2093", "relation": "references"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/europe/amsterdam/2020-2029/quest-007-stroopwafel-quest.md", "visibility": "internal", "audience": ["lore", "narrative", "live_ops"], "risk_level": "low"}, "review": {"chain": [{"role": "lore_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Квест о строопвафлях оставался в Markdown и не описывал традицию уличных рынков Амстердама.", "goal": "Структурировать дегустацию строопвафлей в YAML, подчеркнув ритуал подогрева на кофе.", "essence": "Игрок пробует свежую карамельную вафлю на рынке Albert Cuypmarkt и берёт сладкий сувенир домой.", "key_points": ["Свежие строопвафли отличаются от фабричных вариантов и требуют подачи с горячим напитком.", "Рынки города предлагают аутентичный опыт и ритуал растапливания карамели.", "Квест поддерживает гастрономическую линию и рассказывает об изобретении в Гауде."]}, "content": {"sections": [{"id": "overview", "title": "Контекст", "body": "Строопвафли — символ голландской уличной кухни, изобретённый в Гауде в XVIII веке.\\nКвест демонстрирует связь сладости с повседневными привычками жителей Амстердама.\\n", "mechanics_links": [], "assets": []}, {"id": "quest-steps", "title": "Этапы", "body": "1. Найти рынок Albert Cuypmarkt или аналогичный уличный стенд.\\n2. Заказать свежеприготовленный строопвафель и наблюдать процесс выпечки.\\n3. Положить вафлю на чашку горячего кофе, чтобы карамель прогрелась паром.\\n4. Съесть тёплую вафлю и приобрести упаковку для друзей.\\n5. Занести заметки о гастрономических предпочтениях Амстердама.\\n", "mechanics_links": [], "assets": []}, {"id": "regional-details", "title": "Региональные детали", "body": "Описываются двойные вафли с карамельной начинкой, связь с городом Гауда и популярность уличных рынков.\\nПодчёркиваются советы по подаче, чтобы карамель стала мягкой и ароматной.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "Опыт: 500 XP.\\nВалюта: −5.\\nБафф: +10% Energy на 2 часа.\\nАчивка: «Строопвафельный гурман».\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "Кулинарные и социальные механики показывают традицию уличной еды и покупку сувениров.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"needs_task": false, "github_issue": 55, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-12", "author": "concept_director", "changes": "Конвертация квеста «Квест строопвафлей» в YAML и описание уличного ритуала дегустации."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

