-- Quest: quest-moscow-2039-social-credit
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-moscow-2039-social-credit', 'Москва 2030-2039 — Социальный кредит', 'Игрок живёт под контролем рейтинга, расследует управляющих операторов и решает судьбу цифрового режима Москвы.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-moscow-2039-social-credit", "title": "Москва 2030-2039 — Социальный кредит", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-13T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["moscow", "social-credit", "surveillance"], "topics": ["governance", "reputation-systems"], "related_systems": ["narrative-service", "reputation-service", "economy-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/cis/moscow/2030-2039/quest-020-social-credit.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Московской линии не хватало квеста, объясняющего запуск социальной рейтинговой системы и её влияние на жителей архологии.", "goal": "Показать игроку, как рейтинг регулирует доступ к ресурсам, и дать инструменты для поддержки, манипуляции или саботажа системы.", "essence": "Игрок живёт под контролем рейтинга, расследует управляющих операторов и решает судьбу цифрового режима Москвы.", "key_points": ["Рейтинг меняется от повседневных действий и определяет доступ к зонам, кредитам и социальной защите.", "Расследование позволяет выяснить, кто контролирует алгоритмы и зачем поддерживается система.", "Финальный выбор запускает глобальные изменения для всех миссий в Московской архологии."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Квест знакомит игрока с новой системой социального кредита. Нужно пройти церемонию запуска, получить стартовый рейтинг и понять,\\nкак он влияет на жизнь внутри купола.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Посетить презентацию системы и активировать личный рейтинг.\\n2. Выполнить ежедневные задачи, оценивая как рейтинг открывает или закрывает сервисы.\\n3. Собрать сведения о разработчиках и контролёрах рейтинга через цепочку взломов.\\n4. Внедриться в центр обработки данных и получить доступ к ядру алгоритма.\\n5. Принять решение: поддержать систему, переписать рейтинг под себя или уничтожить инфраструктуру.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Московская архология использует социальный кредит как инструмент дисциплины: высокий рейтинг даёт транспортные льготы,\\nмедицину и элитные товары, низкий — приводит к блокировке перемещений и вмешательству служб безопасности.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- Опыт: 2 500 XP.\\n- Валюта и ресурсы зависят от выбранного сценария.\\n- Глобальное событие, меняющее репутации фракций и доступ к локациям Москвы.\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "Динамический рейтинг, социальные санкции и бонусы, моральные дилеммы, взаимодействие с городскими службами.\\n", "mechanics_links": [], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест связывает линии «Хартия Купола» и сюжеты городской безопасности, открывая миссии по реформе Москвы и сопротивлению.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 883, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-13", "author": "concept_team", "changes": "Квест «Социальный кредит» адаптирован под шаблон Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

