-- Quest: quest-moscow-2060-network-rib
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-moscow-2060-network-rib', 'Москва 2040-2060 — Сетевое ребро', 'Игрок собирает оборудование, устанавливает узлы, прорывается через Blackwall и решает, станет ли ребро общим благом или частным ресурсом.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-moscow-2060-network-rib", "title": "Москва 2040-2060 — Сетевое ребро", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-13T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["moscow", "netrunner", "blackwall"], "topics": ["infrastructure", "hacking"], "related_systems": ["narrative-service", "network-service", "economy-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/cis/moscow/2040-2060/quest-021-network-rib.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "high"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "После фрагментации NET требовался сценарий, показывающий, как хакеры Москвы строят новый канал связи через Blackwall.", "goal": "Организовать постройку защищённого ребра, обеспечивающего связь между куполами и дающего игроку стратегический выбор доступа.", "essence": "Игрок собирает оборудование, устанавливает узлы, прорывается через Blackwall и решает, станет ли ребро общим благом или частным ресурсом.", "key_points": ["Нужны редкие серверы эпохи до DataKrash и установка в стратегических районах столицы.", "Прокладка через Blackwall вызывает ответ NetWatch и боевые сценарии в NET.", "Финальное решение определяет, кто получит выгоду и насколько устойчивой будет новая сеть."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Квест открывает проект «Сетевое ребро»: нужно поддержать хакерский коллектив, восстановить связность Москвы с внешним миром и\\nудержать канал от вмешательства NetWatch.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Встретиться с координатором коллектива и получить архитектуру ребра.\\n2. Найти и выкупить пять серверов pre-DataKrash для узлов сети.\\n3. Установить оборудование в ключевых районах Москвы, защитив точки от рейдов корпораций.\\n4. Провести церемонию взлома через Blackwall (DC 24), отбивая цифровые атаки NetWatch.\\n5. Принять решение о модели доступа: открытой, закрытой или монетизированной.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Москву разрезают сектора глушения и корпоративные фильтры; «Сетевое ребро» соединяет их в единую защищённую шину.\\nПроект требует скрытных маршрутов, резервных генераторов и поддержки местных фракций.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- Опыт: 3 000 XP.\\n- Пассивный доход до 5 000 еддис в месяц при монетизации.\\n- Глобальные торговые маршруты и контакты с другими городами.\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "Высокоуровневое хакерство, установка сетевой инфраструктуры, защита от NetWatch, экономическое управление каналом.\\n", "mechanics_links": [], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест связывает линии глобальной торговли и сетевой войны, открывая миссии обмена данными между мегаполисами и ответ NetWatch.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 884, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-13", "author": "concept_team", "changes": "Квест «Сетевое ребро» адаптирован под шаблон Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

