INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-yerevan-armenian-cognac', 'Ереван 2020-2029 — «Армянский коньяк»', 'Игрок посещает Ереванский коньячный завод, исследует погреба и сравнивает легендарные сорта напитка.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-yerevan-armenian-cognac", "title": "Ереван 2020-2029 — «Армянский коньяк»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["yerevan", "cognac", "gastronomy"], "topics": ["culinary", "heritage"], "related_systems": ["narrative-service", "economy-system", "reputation-system"], "related_documents": [{"id": "canon-region-cis-yerevan-2020-2029", "relation": "contextualizes"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/cis/yerevan/2020-2029/quest-003-armenian-cognac.md", "visibility": "internal", "audience": ["narrative", "quest-design", "live_ops"], "risk_level": "medium"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Markdown-квест о дегустации коньяка не описывал производственные этапы и культурный символизм бренда «Арарат».", "goal": "Структурировать экскурсию по заводу, историю выдержки и социальное событие дегустации.", "essence": "Игрок посещает Ереванский коньячный завод, исследует погреба и сравнивает легендарные сорта напитка.", "key_points": ["Экскурсия объясняет традиции выдержки и технологию.", "Дегустация связывает напиток с историческими личностями, включая Черчилля.", "Успех повышает харизму и открывает гастрономическую линию Армении."]}, "content": {"sections": [{"id": "tour", "title": "Экскурсия по заводу", "body": "Гид проводит через производственные цеха, рассказывая об основании завода в 1887 году.\\nИгрок видит процесс перегонки и узнаёт о дубовых бочках из Карабахского леса.\\n", "mechanics_links": [], "assets": []}, {"id": "cellar", "title": "Погреба выдержки", "body": "- Погружение в подземные хранилища с бочками вековой выдержки.\\n- Истории о специальных партиях, подаренных политикам и дипломатам.\\n", "mechanics_links": [], "assets": []}, {"id": "tasting", "title": "Дегустация", "body": "- Линейка сортов: 3*, 5*, «Наири».\\n- Мини-игра по подбору ароматов и нот.\\n- Сравнение отзывов Черчилля и современных дегустаторов.\\n", "mechanics_links": [], "assets": []}, {"id": "legacy", "title": "Наследие бренда", "body": "Игрок решает, как популяризировать напиток: экспорт, клуб дегустаторов или AR-тур по заводу.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- 2 000 XP.\\n- Валюта: −100 кредов (стоимость дегустации).\\n- Бафф «Charisma +30%» на 12 часов.\\n- Ачивка «Коньячный знаток».\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [{"term": "Арарат", "definition": "Армянский бренд коньяка, основанный в 1887 году и известный качеством выдержанных напитков."}], "references": [], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-12", "author": "concept_director", "changes": "Конвертация квеста «Армянский коньяк» в knowledge-entry."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards,