INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-moscow-2060-offline-bundle', 'Москва 2040-2060 — Офлайн-пакет', 'Игрок получает офлайн-пакет, преодолевает контрольные точки и решает, стоит ли вскрывать или подменять данные ради выгоды.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-moscow-2060-offline-bundle", "title": "Москва 2040-2060 — Офлайн-пакет", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-13T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["moscow", "courier", "data-transfer"], "topics": ["logistics", "data-resilience"], "related_systems": ["narrative-service", "logistics-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/cis/moscow/2040-2060/quest-022-offline-bundle.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Курьерской ветке Москвы не хватало квеста, демонстрирующего физическую доставку данных в условиях фрагментации NET.", "goal": "Дать игроку повторяемый сценарий доставки с моральным выбором — следовать протоколу или вмешаться в содержимое.", "essence": "Игрок получает офлайн-пакет, преодолевает контрольные точки и решает, стоит ли вскрывать или подменять данные ради выгоды.", "key_points": ["Физические носители заменяют цифровые каналы между куполами.", "Вскрытие или подмена пакета даёт дополнительные награды, но рискует репутацией.", "Квест рассчитан на ежедневное повторение и поддерживает логистические экономики Москвы."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Квест погружает в работу курьерской сети Москвы. Нужно получить офлайн-пакет, выбрать маршрут доставки и принять решение,\\nвмешиваться ли в передачу данных.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Забрать запечатанный кейс у оператора и получить инструкции по маршруту.\\n2. Добраться до пункта назначения, минуя посты охраны, перехватчиков и точки проверки.\\n3. Передать пакет получателю или изменить содержимое перед сдачей.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Московские купола соединены физическими коридорами; NetWatch и корпоративные патрули сканируют грузы, а частные охранные фирмы\\nпродают «пропуска». Курьеры используют бронированные кейсы и одноразовые ключи.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- Опыт: 200 XP.\\n- Валюта: 500–1 000 еддис за стандартную доставку.\\n- Репутация: Курьеры +1 при честной сдаче.\\n- Дополнительные награды за вскрытие или подмену, если игрок не разоблачён.\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "Курьерские доставки, выбор риска, вскрытие и подмена данных, репутационные модификаторы.\\n", "mechanics_links": [], "assets": []}, {"id": "connections", "title": "Связи", "body": "Поддерживает ежедневные активности логистической сети Москвы, открывает повторяемые маршруты и влияет на экономики фракций.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 885, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-13", "author": "concept_team", "changes": "Квест «Офлайн-пакет» адаптирован под шаблон Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

