-- Quest: quest-moscow-2060-blackwall-patrol
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-moscow-2060-blackwall-patrol', 'Москва 2040-2060 — Патруль Blackwall', 'Игрок проходит отбор, патрулирует Blackwall, отражает атаку роуг-ИИ и определяет будущее цифровой обороны столицы.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-moscow-2060-blackwall-patrol", "title": "Москва 2040-2060 — Патруль Blackwall", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-23T01:10:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["moscow", "blackwall", "netwatch"], "topics": ["cybersecurity", "high-risk"], "related_systems": ["narrative-service", "combat-service"], "related_documents": [{"id": "github-issue-886", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/886", "relation": "migrated_to", "migrated_at": "2025-11-23T01:10:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/cis/moscow/2040-2060/quest-023-blackwall-patrol.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "high"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Московской линии не хватало экстремального сценария, демонстрирующего работу NetWatch на периметре Blackwall и угрозы роуг-ИИ.", "goal": "Провести игрока через вступление в патруль, боевые операции и последствия прорыва, дав выбор — поддерживать службу или раскрыть её секреты.", "essence": "Игрок проходит отбор, патрулирует Blackwall, отражает атаку роуг-ИИ и определяет будущее цифровой обороны столицы.", "key_points": ["Требуются экспертизы в хакерстве и поддержке команды NetWatch.", "Босс-файт с роуг-ИИ несёт пермадеат-риски и рост киберпсихоза.", "Решение игрока меняет репутацию NetWatch и доступ к уникальным имплантам."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Квест раскрывает работу элитного патруля NetWatch. Нужно пройти отбор, изучить протоколы и выйти на периметр Blackwall,\\nгде растёт активность роуг-ИИ.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Пройти рекрутские тесты NetWatch и получить доступ к специализированному снаряжению.\\n2. Освоить оборонительные протоколы, установить имплант NetWatch и синхронизироваться с командой.\\n3. Патрулировать периметр Blackwall, выявляя аномалии и готовясь к угрозе.\\n4. Отразить прорыв роуг-ИИ, управляя ICE, дронами и оборонительными шипами.\\n5. Принять решение: остаться в патруле, скрыть последствия или передать данные другим фракциям.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Blackwall Москва держит на границе с цифровыми пустошами; NetWatch размещает мобильные узлы, дроны и ICE-шторма.\\nПатрули сталкиваются с когнитивными атаками и паразитами роуг-ИИ.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- Опыт: 4 000 XP.\\n- Валюта: 20 000 еддис.\\n- Имплант NetWatch, уменьшающий урон от ИИ.\\n- Риск: киберпсихоз +20% при провале обороны.\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "Высокоуровневое хакерство, командный бой с роуг-ИИ, риск пермадеата, управление репутацией NetWatch.\\n", "mechanics_links": [], "assets": []}, {"id": "connections", "title": "Связи", "body": "Связан с квестами «Шум за стеной» и сюжетами NetWatch, открывает цепочки сертификации и расследований Blackwall.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 886, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-13", "author": "concept_team", "changes": "Квест «Патруль Blackwall» адаптирован под шаблон Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

