INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-moscow-2040-2060-warm-corridor', 'Москва 2040-2060 — «Тёплый коридор»', 'Игрок строит маршрут из трёх узлов, защищает его от атак и проводит данные к Новосибирску.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-moscow-2040-2060-warm-corridor", "title": "Москва 2040-2060 — «Тёплый коридор»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-23T01:10:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["moscow", "quest", "blackwall"], "topics": ["hacker-content", "logistics"], "related_systems": ["narrative-service", "hacking-service", "defense-service"], "related_documents": [{"id": "github-issue-888", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/888", "relation": "migrated_to", "migrated_at": "2025-11-23T01:10:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/cis/moscow/2040-2060/quest-025-warm-corridor.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Города по ту сторону Blackwall отрезаны и нуждаются в безопасном канале связи.", "goal": "Создать временный «тёплый коридор» для передачи данных через Blackwall и защитить его от ИИ.", "essence": "Игрок строит маршрут из трёх узлов, защищает его от атак и проводит данные к Новосибирску.", "key_points": ["Коридор требует точной картографии и сильного хакерства.", "Игрок обороняет узлы от волн роуг-ИИ.", "Успех открывает торговлю и предоставляет карту безопасных зон."]}, "content": {"sections": [{"id": "briefing", "title": "Заказчик", "body": "Торговец просит провести данные к Новосибирску. Игрок получает вводную о рисках Blackwall и оплате.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}, {"id": "mapping", "title": "Картография Blackwall", "body": "Игрок изучает цифровые карты, ищет слабые зоны. Хакерский чек DC 22 определяет успех и точность маршрута.\\n", "mechanics_links": ["mechanics/hacking/blackwall-mapping.yaml"], "assets": []}, {"id": "setup", "title": "Создание коридора", "body": "Требуется развернуть три узла в определённых точках. Мини-игра по установке узлов и настройке каналов.\\n", "mechanics_links": ["mechanics/building/network-construction.yaml"], "assets": []}, {"id": "defense", "title": "Оборона", "body": "Роуг-ИИ атакуют узлы волнами. Игрок управляет защитным софтом, дронами и ловушками.\\n", "mechanics_links": ["mechanics/combat/tower-defense-hacking.yaml"], "assets": []}, {"id": "transfer", "title": "Передача данных", "body": "После успешной обороны игрок выполняет передачу данных, следя за стабильностью в течение 30 минут.\\n", "mechanics_links": ["mechanics/hacking/time-limited-channel.yaml"], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- 2 500 XP, 15 000 еддис, карта Blackwall (показывает безопасные зоны).\\n- Торговец открывает новые маршруты и задания.\\n", "mechanics_links": ["mechanics/progression/progression-perks.yaml"], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 888, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-13", "author": "concept_team", "changes": "Конвертация квеста «Тёплый коридор» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

