INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-berlin-brandenburg-gate', 'Берлин 2020-2029 — «Бранденбургские ворота»', 'Игрок получает груз, проходит Pariser Platz и уводит товар через ворота, балансируя между подкупом и скрытностью.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-berlin-brandenburg-gate", "title": "Берлин 2020-2029 — «Бранденбургские ворота»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["berlin", "smuggling", "landmarks"], "topics": ["stealth", "crime"], "related_systems": ["narrative-service", "stealth-system", "economy-system"], "related_documents": [{"id": "canon-region-europe-berlin-2020-2093", "relation": "contextualizes"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/europe/berlin/2020-2029/quest-003-brandenburg-gate.md", "visibility": "internal", "audience": ["narrative", "quest-design", "economy"], "risk_level": "medium"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Markdown-версия не формализовала стелс-провоз через символ единства Германии.", "goal": "Описать контрабандную операцию под историческими арками с вариантами прохождения.", "essence": "Игрок получает груз, проходит Pariser Platz и уводит товар через ворота, балансируя между подкупом и скрытностью.", "key_points": ["Сцена под квадригой Виктории связывает прошлое разделения с современным криминалом.", "Проверки стелса и подкупа предлагают разные ветки наград.", "Успех усиливает подпольную репутацию Берлина."]}, "content": {"sections": [{"id": "setup", "title": "Подготовка операции", "body": "Игрок принимает заказ у контрабандной сети и изучает маршрут через Pariser Platz.\\nАнализ охраны показывает смены, дроны и камеры, доступные для взлома.\\nВозможен выбор напарника: хакер, дипломат или курьер.\\n", "mechanics_links": [], "assets": []}, {"id": "execution", "title": "Провоз груза", "body": "- Стелс-ветка: использовать тени колонн и отключать датчики через импланты.\\n- Подкуп: передать охране цифровые ваучеры, получая дебафф к будущему доверию.\\n- Вариант с отвлечением: уличный артист запускает перформанс для отвода внимания туристов.\\n", "mechanics_links": [], "assets": []}, {"id": "payoff", "title": "Завершение и награды", "body": "- 1 500 XP, 2 500 кредов и +3 к репутации контрабандистов при скрытном успехе.\\n- Лог записи «Tor der Freiheit» добавляет исторический контекст.\\n- Провал вызывает розыск и снижает доступ к центральному району.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [{"term": "Pariser Platz", "definition": "Площадь перед Бранденбургскими воротами, ключевой узел центра Берлина."}], "references": [], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-12", "author": "concept_director", "changes": "Конвертация квеста «Бранденбургские ворота» в knowledge-entry."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

