INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-moscow-2060-red-market-baron', 'Москва 2040-2060 — Барон Красного рынка', 'Игрок выполняет контракты, устраняет конкурентов и захватывает торговые зоны, становясь бароном.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-moscow-2060-red-market-baron", "title": "Москва 2040-2060 — Барон Красного рынка", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-23T01:10:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["moscow", "economy", "black-market"], "topics": ["economic_control", "progression_longform"], "related_systems": ["narrative-service", "economy-service", "territory-control-service"], "related_documents": [{"id": "github-issue-889", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/889", "relation": "migrated_to", "migrated_at": "2025-11-23T01:10:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/cis/moscow/2040-2060/quest-026-red-market-baron.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "В поздней линии Москвы не хватало контента про управление теневой экономикой.", "goal": "Предоставить игроку путь к контролю «Красного рынка» и пассивному доходу.", "essence": "Игрок выполняет контракты, устраняет конкурентов и захватывает торговые зоны, становясь бароном.", "key_points": ["Квест разворачивает долгосрочную экономическую прогрессию.", "Пассивный доход и контроль цен меняют экономику региона.", "Барон открывает редкие товары и уникальные события."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "«Красный рынок» — крупнейшая теневая экономика Москвы. Игрок начинает в роли мелкого торговца, выполняет цепочки контрактов\\nи продвигается вверх по иерархии, защищая свои активы от конкурентов и корпораций.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Получить доступ к рынку, работая торговцем.\\n2. Выполнить серию из десяти контрактов, повышающих репутацию.\\n3. Устранить трёх конкурентов через бизнес-войну или силовые методы.\\n4. Захватить торговую зону, обеспечив защиту и логистику.\\n5. Получить титул барона, получать пассивный доход и регулировать цены.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- Опыт: 3 000 XP.\\n- Пассивный доход: 10 000 Eddies в неделю.\\n- Титул «Барон Красного рынка» и возможность управлять ценами.\\n- Доступ к эксклюзивному магазину редких товаров.\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "Экономическая игра, территориальный контроль, защита активов, события LiveOps рынков, рейтинговые таблицы по доходности.\\n", "mechanics_links": [], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест связан с рейдом на чёрный рынок и другими экономическими линиями, влияя на поставки и цены региона.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 889, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-13", "author": "concept_team", "changes": "Квест «Барон Красного рынка» адаптирован под шаблон Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

