INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-moscow-2060-nomad-caravan', 'Москва 2040-2060 — Караван номадов', 'Игрок готовит транспорт, сопровождает караван Москва → Санкт-Петербург и отражает угрозы.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-moscow-2060-nomad-caravan", "title": "Москва 2040-2060 — Караван номадов", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-23T01:10:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["moscow", "nomads", "convoy"], "topics": ["cooperative_play", "travel_adventure"], "related_systems": ["narrative-service", "convoy-service", "weather-service"], "related_documents": [{"id": "github-issue-890", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/890", "relation": "migrated_to", "migrated_at": "2025-11-23T01:10:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/cis/moscow/2040-2060/quest-027-nomad-caravan.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Отсутствовал кооперативный контент, показывающий кочевые караваны между мегаполисами.", "goal": "Дать игрокам роль охраны, механиков и медиков в номадском караване.", "essence": "Игрок готовит транспорт, сопровождает караван Москва → Санкт-Петербург и отражает угрозы.", "key_points": ["Маршрут включает случайные события и погодные испытания.", "Кооператив усиливает экономику и социальные связи с номадами.", "Успех даёт доступ к уникальным товарам и услугам."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Караван ищет команду сопровождения. Игрок выбирает роль (охранник, механик, медик), модернизирует технику и отправляется с\\nкараваном по трассе вдоль разрушенной магистрали.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Подписать контракт и получить роль.\\n2. Подготовить транспорт, закупить запчасти и лекарства.\\n3. Совершить путь Москва → Санкт-Петербург, сталкиваясь с угрозами и поломками.\\n4. Защитить груз от бандитов и пограничных рейдеров.\\n5. Доставить караван и получить оплату и репутацию.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- 1 500 XP, 5 000 Eddies, репутация номадов +3.\\n- Доступ к номадским сервисам, тюнингу и уникальным контрактам.\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "Мобильная защита, кооперативные роли, случайные события, погодная система, LiveOps караваны, внутриигровые рейтинги по\\nэффективности охраны.\\n", "mechanics_links": [], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест открывает номадские сюжетные линии региона и влияет на торговые цепочки между городами.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 890, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-13", "author": "concept_team", "changes": "Квест «Караван номадов» адаптирован под шаблон Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EX