-- Quest: quest-moscow-2060-memory-trade
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-moscow-2060-memory-trade', 'Москва 2040-2060 — Торговля памятью', 'Встреча с брокером памяти, продажа собственных воспоминаний и покупка чужих эмоций и навыков.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-moscow-2060-memory-trade", "title": "Москва 2040-2060 — Торговля памятью", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-13T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["moscow", "memory-market", "emotional-economy"], "topics": ["experimental_economy", "emotional_gameplay"], "related_systems": ["narrative-service", "wellbeing-service", "economy-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/cis/moscow/2040-2060/quest-028-memory-trade.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Серия Москвы не показывала рынок воспоминаний и его влияние на психологию персонажа.", "goal": "Дать игроку возможность торговать памятью и испытывать эмоциональные последствия.", "essence": "Встреча с брокером памяти, продажа собственных воспоминаний и покупка чужих эмоций и навыков.", "key_points": ["Выбор между продажей счастливых и болезненных воспоминаний меняет состояние героя.", "Покупка памяти даёт временные навыки, но стоит дорого.", "Квест связывает лор Ковчега памяти и эмоциональные арки персонажа."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "В подземном рынке Москвы действует брокер памяти. Игрок может избавиться от травм, заработать на счастливых кадрах или купить\\nчужие эмоции и навыки для временного усиления.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Встретиться с брокером памяти и узнать правила торговли.\\n2. Выбрать, какие воспоминания продать и по какой цене.\\n3. Приобрести чужие воспоминания, влияющие на навыки и эмоции.\\n4. Пережить воспоминание через интерактивную сцену и заметить последствия.\\n", "mechanics_links": [], "assets": []}, {"id": "choices", "title": "Выборы", "body": "Продажа счастливых воспоминаний приносит высокий доход, но снижает настроение. Продажа болезненных избавляет от дебаффов.\\nПокупка навыков даёт бонусы на 24 часа, а эмоции усиливают ролевые сцены.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- Опыт: 1 000 XP.\\n- Финансы: от -5 000 до +20 000 Eddies в зависимости от сделок.\\n- Уникальные эффекты: временные навыки, эмоциональные статусы, новые сюжетные ветки.\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "Экономика воспоминаний, временные баффы, эмоциональные дебаффы, интеграция с системой благополучия и LiveOps выставок памяти.\\n", "mechanics_links": [], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест связан с Ковчегом памяти и другими сюжетами о данных и эмоциях, влияя на репутацию среди нетраннеров и психологов.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 838, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-13", "author": "concept_team", "changes": "Квест «Торговля памятью» адаптирован под шаблон Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

