INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-yerevan-genocide-memorial', 'Ереван 2020-2029 — «Мемориал геноцида»', 'Игрок поднимается к Цицернакаберду, изучает музей геноцида и отдает дань памяти у вечного огня.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-yerevan-genocide-memorial", "title": "Ереван 2020-2029 — «Мемориал геноцида»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["yerevan", "genocide", "memorial"], "topics": ["remembrance", "history"], "related_systems": ["narrative-service", "history-system", "reputation-system"], "related_documents": [{"id": "canon-region-cis-yerevan-2020-2029", "relation": "contextualizes"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/cis/yerevan/2020-2029/quest-002-genocide-memorial.md", "visibility": "internal", "audience": ["narrative", "quest-design", "education"], "risk_level": "high"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Markdown-квест не раскрывал структуру визита в Цицернакаберд и эмоциональные механики памяти.", "goal": "Сформулировать посещение мемориала как ритуал памяти с образовательным сопровождением и культурными эффектами.", "essence": "Игрок поднимается к Цицернакаберду, изучает музей геноцида и отдает дань памяти у вечного огня.", "key_points": ["Исторический контекст трагедии 1915 года подается через экспозиции и аудиогиды.", "Вечный огонь и 12 плит провинций формируют ритуал скорби.", "Завершение квеста усиливает репутацию культуры памяти и открывает глобальные мемориальные линии."]}, "content": {"sections": [{"id": "ascent", "title": "Подъем к мемориалу", "body": "Игрок проходит по аллее памяти, наблюдая высаженные деревья и цитаты свидетелей.\\nАудиогид рассказывает о разрушенных провинциях и судьбах армян.\\n", "mechanics_links": [], "assets": []}, {"id": "museum", "title": "Музей геноцида", "body": "- Экспозиции фотографий, документов и личных историй.\\n- Интерактивные столы с картой депортаций и дипломатическими реакциями мира.\\n- Видеозал с хроникой признания геноцида.\\n", "mechanics_links": [], "assets": []}, {"id": "eternal_flame", "title": "Вечный огонь", "body": "- Спуск к вечному огню и 12 плитам, символизирующим утраченные вилайеты.\\n- Минуты молчания, в ходе которых игрок активирует эмпатию и получает бафф «Мы помним».\\n", "mechanics_links": [], "assets": []}, {"id": "reflection", "title": "Рефлексия и выбор", "body": "Игрок выбирает способ почтить память: оставить цифровую запись, посадить дерево или организовать лекцию в городе.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- 5 000 XP.\\n- Валюта: 0.\\n- Эмоциональный эффект «Скорбь и память».\\n- Ачивка «Мы помним».\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [{"term": "Цицернакаберд", "definition": "Мемориальный комплекс в Ереване, посвященный жертвам геноцида армян 1915 года."}], "references": [], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-12", "author": "concept_director", "changes": "Конвертация квеста «Мемориал геноцида» в knowledge-entry."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

