INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-moscow-2060-shelter-life', 'Москва 2040-2060 — Жизнь в укрытии', 'Игрок прячется в бункере, проводит время в социальных активностях и выходит, когда буря стихает.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-moscow-2060-shelter-life", "title": "Москва 2040-2060 — Жизнь в укрытии", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-13T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["moscow", "shelter", "slice-of-life"], "topics": ["social_simulation", "atmospheric_storytelling"], "related_systems": ["narrative-service", "wellbeing-service", "social-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/cis/moscow/2040-2060/quest-030-shelter-life.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Нужно показать атмосферу «Time of the Red» через симуляцию жизни в бункере.", "goal": "Позволить игроку пережить красную бурю в укрытии и познакомиться с жителями.", "essence": "Игрок прячется в бункере, проводит время в социальных активностях и выходит, когда буря стихает.", "key_points": ["В укрытии доступны карточные игры, обмен историями, романтические сцены.", "Буря заставляет погрузиться в повседневность и чувство общности.", "Квест поддерживает социальные LiveOps события и создаёт новые контакты."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Во время красной бури город переводится в режим укрытий. Игрок спускается в общественный бункер, где можно переждать бурю,\\nобщаясь с NPC и другими игроками.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Получить уведомление о буре и добраться до бункера.\\n2. Зарегистрироваться у коменданта и получить койко-место.\\n3. Провести 48 часов игрового времени (или эквивалент) в укрытии, участвуя в активностях.\\n4. Вернуться на поверхность после окончания бури.\\n", "mechanics_links": [], "assets": []}, {"id": "activities", "title": "Активности", "body": "Карточные игры, обмен историями, романтические взаимодействия, торговля ресурсами, участие в мини-ивентах и конкурсах.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- Опыт: 500 XP.\\n- Социальные контакты, новые союзники и потенциальные романтические линии.\\n- Выигрыш в азартных мини-играх, доступ к LiveOps мероприятиям бункера.\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "Slice of life, социальное взаимодействие, мини-игры, управление ресурсами в укрытии, длительные события LiveOps.\\n", "mechanics_links": [], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест расширяет лор «Time of the Red», связывает социальные линии и события выживания в Москве.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 840, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-13", "author": "concept_team", "changes": "Квест «Жизнь в укрытии» адаптирован под шаблон Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements