INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-yerevan-khor-virap', 'Ереван 2020-2029 — «Монастырь Хор Вирап»', 'Игрок отправляется в паломничество, спускается в темницу Григория Просветителя и переживает символ веры Армении.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-yerevan-khor-virap", "title": "Ереван 2020-2029 — «Монастырь Хор Вирап»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["yerevan", "khor-virap", "pilgrimage"], "topics": ["faith", "history"], "related_systems": ["narrative-service", "pilgrimage-system", "reputation-system"], "related_documents": [{"id": "canon-region-cis-yerevan-2020-2029", "relation": "contextualizes"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/cis/yerevan/2020-2029/quest-004-khor-virap-monastery.md", "visibility": "internal", "audience": ["narrative", "quest-design", "tourism"], "risk_level": "low"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Markdown-квест не раскрывал паломнические ветки и религиозную важность Хор Вирапа.", "goal": "Описать путешествие к монастырю у подножия Арарата с историей принятия христианства в Армении.", "essence": "Игрок отправляется в паломничество, спускается в темницу Григория Просветителя и переживает символ веры Армении.", "key_points": ["Дорога из Еревана подчеркивает близость к Арарату и пограничный ландшафт.", "Темница Григория Просветителя раскрывает историю обращения страны в христианство в 301 году.", "Квест завершает туристический фотосет и религиозные баффы."]}, "content": {"sections": [{"id": "journey", "title": "Дорога к монастырю", "body": "Игрок организует поездку на 30 км на юг от Еревана, наблюдая Арарат и ландшафты Араратской долины.\\nПо пути активируются диалоги о религиозной истории региона.\\n", "mechanics_links": [], "assets": []}, {"id": "monastery", "title": "Монастырский комплекс", "body": "- Ознакомление с архитектурой Хор Вирапа и панорамой на Арарат.\\n- Общение с монахами, предлагающими задания на благотворительные пожертвования.\\n", "mechanics_links": [], "assets": []}, {"id": "descent", "title": "Спуск в темницу", "body": "- Игрок спускается в 6-метровую яму, где Григорий Просветитель провёл 13 лет.\\n- Внутри запускается кинематографическая сцена с рассказом о принятии христианства.\\n", "mechanics_links": [], "assets": []}, {"id": "reflection", "title": "Ритуал и фотосессия", "body": "После молитвы игрок делает культовые снимки монастыря на фоне Арарата и делится ими в городских сетях, усиливая веру жителей.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- 2 500 XP.\\n- Валюта: 0.\\n- +20 к параметру «Культура».\\n- Ачивка «Паломник Хор Вирапа».\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [{"term": "Григорий Просветитель", "definition": "Святой, который обратил Армению в христианство в 301 году после заточения в Хор Вирапе."}], "references": [], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-12", "author": "concept_director", "changes": "Конвертация квеста «Монастырь Хор Вирап» в knowledge-entry."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.leve