-- Quest: quest-dallas-2029-001-jfk-memorial
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-dallas-2029-001-jfk-memorial', 'Даллас — мемориал JFK', 'Игрок исследует ключевые точки трагедии JFK, участвует в интерактивном архиве музея и укрепляет репутацию среди историков-коллекционеров.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-dallas-2029-001-jfk-memorial", "title": "Даллас — мемориал JFK", "document_type": "canon", "category": "narrative", "status": "approved", "version": "0.1.0", "last_updated": "2025-11-11T06:20:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["dallas", "history", "memorial"], "topics": ["city_storylines", "historical_events"], "related_systems": ["narrative-service", "exploration-service"], "related_documents": [{"id": "master-timeline", "relation": "references"}, {"id": "quest-system", "relation": "aligns"}, {"id": "github-issue-331", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/331", "relation": "migrated_to", "migrated_at": "2025-11-22T19:45:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/america/dallas/2020-2029/quest-001-jfk-memorial.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Оригинальный текст фиксировал факты мемориала JFK, но не связывал их с эмоциональной аркой и игровыми последствиями.", "goal": "Сделать квест про посещение Dealey Plaza эмоциональной кульминацией исторической линии Далласа с понятными механиками и наградами.", "essence": "Игрок исследует ключевые точки трагедии JFK, участвует в интерактивном архиве музея и укрепляет репутацию среди историков-коллекционеров.", "key_points": ["Квест подчёркивает переход от экскурсии к личному осмыслению трагедии.", "В сценарий встроены исторические объекты Dealey Plaza и Sixth Floor Museum.", "Эмоциональная сцена усиливает социальные связи с фракцией «Историки Далласа»."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Сценарий выводит игрока к мемориалу JFK в Dealey Plaza, где каждый шаг соответствует хронологии событий 22 ноября 1963 года.\\nОсмотр «X» на Elm Street, Grassy Knoll и зала шестого этажа сопровождается аудиогідом и выбором реакции на раскрытие подробностей.\\nИгрок проходит от роли наблюдателя к свидетелю, что влияет на репутацию внутри исторической фракции города.\\n", "mechanics_links": ["mechanics/quests/quest-system.yaml"], "assets": []}, {"id": "regional_highlights", "title": "Региональные отсылки", "body": "Dealey Plaza и Sixth Floor Museum остаются центральными объектами мемориала. Маркировка «X» на Elm Street, экспозиция\\nвинтовки и хроника Zapruder позволяют прожить трагедию через реальные артефакты. Теории заговора и дискуссии свидетелей\\nподдерживают культурный контекст Далласа.\\n", "mechanics_links": [], "assets": []}, {"id": "quest_flow", "title": "Этапы квеста", "body": "1. Прибытие на Dealey Plaza и взаимодействие с историческим маршрутом.\\n2. Осмотр отметки «X», выбор реакции игрока и сбор эмоционального артефакта.\\n3. Подъём в Sixth Floor Museum, просмотр восстановленного окна Освальда и хроники Zapruder.\\n4. Разговор с гидом или ветераном охраны, задающим вопросы о теории заговора.\\n5. Финальное решение: поддержать официальную версию или разделить сомнения, что влияет на репутацию.\\n", "mechanics_links": ["mechanics/social/social-mechanics-overview.yaml"], "assets": []}, {"id": "rewards", "title": "Награды и эффекты", "body": "Квест приносит 5000 опыта, снижает валютный баланс на 20 единиц за билеты и сувениры, повышает показатель «История» на 30.\\nОткрывается ачивка «Свидетель трагедии» и доступ к архиву редких хроник.\\n", "mechanics_links": [], "assets": []}, {"id": "integration", "title": "Интеграции и последствия", "body": "Narrative-service добавляет новые события в исторической линии, exploration-service раскрывает дополнительные маршруты по\\nцентру Далласа, а социальные механики сохраняют выбор игрока в базе доверия фракции.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 331, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-11", "author": "concept_team", "changes": "Квест «Мемориал JFK» структурирован в формате YAML и связался с исторической линией Далласа."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

