INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-moscow-2061-2077-proxy-war', 'Москва 2061-2077 — Серый театр', 'Игроки выполняют контракт корпорации А, планируют налёт, сталкиваются с ответом корпорации Б и экстрагируют трофеи.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-moscow-2061-2077-proxy-war", "title": "Москва 2061-2077 — Серый театр", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-13T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["moscow", "proxy-war", "corporations"], "topics": ["faction_conflict", "cooperative_tactics"], "related_systems": ["narrative-service", "combat-service", "faction-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/cis/moscow/2061-2077/quest-032-proxy-war.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "high"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Нужен командный сценарий, раскрывающий скрытые войны корпораций.", "goal": "Дать команде игроков участвовать в прокси-конфликтах и определять исход корпоративных разборок.", "essence": "Игроки выполняют контракт корпорации А, планируют налёт, сталкиваются с ответом корпорации Б и экстрагируют трофеи.", "key_points": ["Миссия поддерживает разные режимы (PvE или PvP) в зависимости от вмешательства противника.", "Решения влияют на репутацию и доступ к корпоративным ресурсам.", "Сценарий повторяемый с новыми целями и корпорациями."]}, "content": {"sections": [{"id": "contract", "title": "Контракт", "body": "Корпорация А нанимает игроков, предоставляет данные разведки и ставит цель — ликвидировать объект корпорации Б.\\n", "mechanics_links": [], "assets": []}, {"id": "planning", "title": "Планирование операции", "body": "Команда распределяет роли, выбирает маршрут и готовит оборудование. Выбор тактики влияет на сложность штурма.\\n", "mechanics_links": [], "assets": []}, {"id": "assault", "title": "Штурм и оборона", "body": "Игроки атакуют объект, сталкиваются с охраной и возможными вмешательствами других игроков, затем удерживают позицию до завершения задачи.\\n", "mechanics_links": [], "assets": []}, {"id": "extraction", "title": "Экстракция", "body": "После захвата цели необходимо покинуть зону с данными или трофеями, избегая подкреплений корпорации Б.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- 3 000 XP, 25 000 Eddies, репутация корпорации-нанимателя +5, репутация цели -5.\\n- Возможность получить редкую корп-экипировку и новые контракты.\\n", "mechanics_links": [], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест интегрируется в корпоративные войны, влияет на LiveOps конфликты и доступ к фракционным магазинам.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 842, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-13", "author": "concept_team", "changes": "Квест «Серый театр» адаптирован под шаблон Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.conten