INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-yerevan-dolma-feast', 'Ереван 2020-2029 — «Пир долмы»', 'Игрок присоединяется к армянской семье, готовит долму и участвует в праздничном застолье.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-yerevan-dolma-feast", "title": "Ереван 2020-2029 — «Пир долмы»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["yerevan", "cuisine", "family"], "topics": ["culinary", "social"], "related_systems": ["narrative-service", "culinary-system", "social-system"], "related_documents": [{"id": "canon-region-cis-yerevan-2020-2029", "relation": "contextualizes"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/cis/yerevan/2020-2029/quest-006-dolma-feast.md", "visibility": "internal", "audience": ["narrative", "quest-design", "live_ops"], "risk_level": "low"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Описание приготовления долмы не фиксировало семейные взаимодействия и механики кулинарного мастер-класса.", "goal": "Зафиксировать традиционный ужин с обучением, социальными бонусами и эффектом на параметры игрока.", "essence": "Игрок присоединяется к армянской семье, готовит долму и участвует в праздничном застолье.", "key_points": ["Процесс готовки раскрывает ингредиенты и технику заворачивания листьев.", "Семейное общение усиливает социальную репутацию.", "Дегустация даёт бафф здоровья и открывает новые рецепты кухни."]}, "content": {"sections": [{"id": "invitation", "title": "Приглашение в дом", "body": "Игрок получает приглашение от семьи и приносит подарки: специи, вино или десерт.\\nВыбор подарка влияет на настроение хозяев.\\n", "mechanics_links": [], "assets": []}, {"id": "cooking", "title": "Процесс приготовления", "body": "- Подготовка фарша, виноградных листьев и специй.\\n- Мини-игра по заворачиванию долмы с оценкой точности.\\n- Обсуждение происхождения блюда и региональных вариантов.\\n", "mechanics_links": [], "assets": []}, {"id": "dinner", "title": "Семейный ужин", "body": "- Совместная трапеза с мацуном и тостами.\\n- Диалоги о традициях, музыке и семейных рецептах.\\n- Возможность записать рецепт или предложить собственный вариант.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- 800 XP.\\n- Валюта: 0.\\n- Бафф «+20% HP на 8 часов».\\n- Ачивка «Мастер долмы».\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [{"term": "Долма", "definition": "Традиционное блюдо армянской кухни из виноградных листьев, фарша и специй, подаётся с мацуном."}], "references": [], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-12", "author": "concept_director", "changes": "Конвертация квеста «Пир долмы» в knowledge-entry."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

