INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-moscow-2061-2077-corpo-life', 'Москва 2061-2077 — Корпоративная жизнь', 'Игрок выполняет офисные задачи, участвует в политике корпораций и управляет отделом на вершине карьерной лестницы.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-moscow-2061-2077-corpo-life", "title": "Москва 2061-2077 — Корпоративная жизнь", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-13T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["moscow", "corporate", "career"], "topics": ["social_simulation", "corporate_intrigue"], "related_systems": ["narrative-service", "social-service", "economy-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/cis/moscow/2061-2077/quest-034-corpo-life.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Не хватало симуляции корпоративной карьеры с интригами и социальными дилеммами.", "goal": "Позволить игроку подняться от стажёра до топ-менеджера, выбирая между честной работой и интригами.", "essence": "Игрок выполняет офисные задачи, участвует в политике корпораций и управляет отделом на вершине карьерной лестницы.", "key_points": ["Пять ступеней роста с уникальными задачами и мини-играми.", "Выбор честного пути, интриг или предательства влияет на репутацию и риски.", "Финал даёт доступ к корпоративным ресурсам и LiveOps бизнес-событиям."]}, "content": {"sections": [{"id": "onboarding", "title": "Вступление в корпорацию", "body": "Игрок проходит собеседование, изучает корпоративный кодекс и занимает стартовую позицию стажёра.\\n", "mechanics_links": [], "assets": []}, {"id": "routine", "title": "Офисная рутина", "body": "Мини-игры на выполнение задач, управленческие решения и KPI-требования формируют ежедневную работу.\\n", "mechanics_links": [], "assets": []}, {"id": "intrigue", "title": "Корпоративные интриги", "body": "Игрок участвует в офисной политике, выбирает союзников и соперников, решает, идти ли на компрометирующие шаги ради повышения.\\n", "mechanics_links": [], "assets": []}, {"id": "promotion", "title": "Карьерный рост", "body": "Серия испытаний для перехода на новый уровень: презентации, управленческие кризисы, защита интересов департамента.\\n", "mechanics_links": [], "assets": []}, {"id": "director", "title": "Управление отделом", "body": "На вершине игрок получает собственный офис, команду NPC и доступ к корпоративным ресурсам, влияя на стратегию компании.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- 2 500 XP, зарплата от 5 000 до 50 000 Eddies в неделю.\\n- Корпоративный статус, доступ к ресурсам и эксклюзивным заданиям.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 844, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-13", "author": "concept_team", "changes": "Квест «Корпоративная жизнь» адаптирован под шаблон Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches,