INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-moscow-2061-2077-implant-addiction', 'Москва 2061-2077 — Имплантная зависимость', 'После диагноза игрок испытывает симптомы, выбирает терапию, удаление имплантов или эксперимент, либо принимает психоз.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-moscow-2061-2077-implant-addiction", "title": "Москва 2061-2077 — Имплантная зависимость", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-13T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["moscow", "cyberpsychosis", "healthcare"], "topics": ["wellbeing_management", "augmentation_risk"], "related_systems": ["narrative-service", "wellbeing-service", "implants-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/cis/moscow/2061-2077/quest-035-implant-addiction.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Требовался социальный квест о последствиях избыточных имплантов и риске киберпсихоза.", "goal": "Показать, как игрок лечит имплантную зависимость, выбирая подходящий метод.", "essence": "После диагноза игрок испытывает симптомы, выбирает терапию, удаление имплантов или эксперимент, либо принимает психоз.", "key_points": ["Каждое лечение меняет статистику и социальные отношения.", "Игнорирование приводит к глубокой стадии киберпсихоза.", "Квест связывает системы здоровья и имплантов."]}, "content": {"sections": [{"id": "diagnosis", "title": "Диагноз", "body": "Врач фиксирует начальную стадию киберпсихоза, вводит ограничения и объясняет варианты лечения.\\n", "mechanics_links": [], "assets": []}, {"id": "symptoms", "title": "Симптомы", "body": "Игрок сталкивается с галлюцинациями, вспышками агрессии и дебаффами, влияющими на бой и социальные сцены.\\n", "mechanics_links": [], "assets": []}, {"id": "treatment", "title": "Варианты лечения", "body": "Терапия, удаление имплантов, экспериментальное вмешательство или отказ от лечения — каждый выбор меняет последствия и стоимость.\\n", "mechanics_links": [], "assets": []}, {"id": "outcomes", "title": "Итоги", "body": "Результат лечения определяет здоровье персонажа, его отношения с врачами и доступ к новым имплантам.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- 1 500 XP.\\n- Финансовые затраты до 10 000 Eddies в зависимости от метода.\\n- Снятие или закрепление статуса киберпсихоза.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 845, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-13", "author": "concept_team", "changes": "Квест «Имплантная зависимость» адаптирован под шаблон Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

