-- Quest: quest-moscow-2077-underground-arena
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-moscow-2077-underground-arena', 'Москва 2061-2077 — Подземная арена', 'Игрок оплачивает участие, настраивает снаряжение, проходит пять раундов и решает, поддерживать ли подполье или выдать его властям.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-moscow-2077-underground-arena", "title": "Москва 2061-2077 — Подземная арена", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-13T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["moscow", "arena", "underground"], "topics": ["combat-events", "liveops"], "related_systems": ["narrative-service", "combat-service", "economy-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/cis/moscow/2061-2077/quest-036-underground-arena.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Игрокам периода 2061-2077 нужен регулярный боевой ивент с экономикой ставок и прогрессией сложности.", "goal": "Организовать подпольный турнир архологии, где участники сражаются за титул и призовой банк.", "essence": "Игрок оплачивает участие, настраивает снаряжение, проходит пять раундов и решает, поддерживать ли подполье или выдать его властям.", "key_points": ["Входной взнос и ставки формируют призовой банк и социальное давление на участника.", "Каждая стадия усиливает противников и вводит модификаторы арены.", "Победа даёт титул и легендарное оружие, поражение снижает репутацию и лишает взноса."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Квест погружает в подпольный бойцовский клуб Москвы. Нужно найти брокера, оплатить участие и выбрать схему ставок,\\nпрежде чем выйти на арену.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Зарегистрироваться у брокера арены, внести взнос 2 000 еддис и настроить ставки.\\n2. Подготовить loadout: выбрать оружие, импланты, арендовать редкие модули.\\n3. Пройти пять раундов 1 на 1 с возрастающей сложностью и уникальными противниками.\\n4. Победить чемпиона арены в динамической финальной сцене с изменяющейся геометрией.\\n5. Решить, сохранить ли тайну арены, инсценировать поражение ради части банка или выдать подполье NetWatch.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Арена спрятана под коммерческим куполом; доступ к ней контролируется подпольными букмекерами. Сектора арены оснащены газовыми\\nфорсунками, магнитными щитами и лазерными решётками, активируемыми по сценарию.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- Победа: 50 000 еддис, титул «Чемпион арены», легендарное оружие ближнего боя.\\n- Поражение: потеря взноса, репутация подполья -1.\\n- Сдача властям: уникальный бонус NetWatch и закрытие арены.\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "Подпольные ставки, ladder-боёвка, финальный босс, вариативные модификаторы арены, опции саботажа или сотрудничества с NetWatch.\\n", "mechanics_links": [], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест поддерживает боевую линию Москвы и liveops-турниры, влияет на прибыль подполья и взаимоотношения с NetWatch и криминальными фракциями.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 846, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-13", "author": "concept_team", "changes": "Квест «Подземная арена» адаптирован под шаблон Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

