-- Quest: quest-moscow-2077-simulation-hint
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-moscow-2077-simulation-hint', 'Москва 2061-2077 — Край симуляции', 'Игрок фиксирует глюки, собирает паттерн координат и находит бункер с сообщением «Вы в симуляции», решая, как использовать знание.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-moscow-2077-simulation-hint", "title": "Москва 2061-2077 — Край симуляции", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-13T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["moscow", "simulation", "anomalies"], "topics": ["main-plot", "mystery"], "related_systems": ["narrative-service", "exploration-service", "mystery-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/cis/moscow/2061-2077/quest-037-simulation-hint.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "high"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Требовался кульминационный квест, раскрывающий идею симуляции и запускающий метасюжет 2078+.", "goal": "Дать игроку цепочку аномалий, которые приводят к терминалу с правдой о симуляции, и предложить выбор дальнейших действий.", "essence": "Игрок фиксирует глюки, собирает паттерн координат и находит бункер с сообщением «Вы в симуляции», решая, как использовать знание.", "key_points": ["Аномалии раскиданы по Москве и требуют наблюдательности и дешифровки.", "Финальная сцена раскрывает основу мира и открывает новую сюжетную ветку.", "Выбор определяет реакцию властей и доступ к миссиям «Выход из симуляции»."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Квест начинает линию метасюжета: игрок сталкивается с глюками реальности, собирает данные и выходит на заброшенный бункер,\\nгде раскрывается истина о симуляции.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Зафиксировать первую аномалию и открыть журнал расследования.\\n2. Найти десять точек с глюками, выполняя мини-головоломки и проверки навыков.\\n3. Сопоставить координаты и расшифровать паттерн, ведущий в бункер.\\n4. Добраться до бункера и активировать терминал с сообщением о симуляции.\\n5. Принять решение: принять истину, отвергнуть её или сообщить властям.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Аномалии появляются в разных слоях архологии: голографические сбои, NPC с повторяющимися репликами, дестабилизированные\\nрайоны. Бункер скрыт под старым промышленным сектором.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- 10 000 XP, уникальный флаг знания о симуляции.\\n- Открытие сюжетной ветки «Выход из симуляции» и изменение отношения властей.\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "Исследование аномалий, сбор подсказок, расшифровка паттернов, сюжетный выбор с последствиями для метасюжета.\\n", "mechanics_links": [], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест связывает период 2061-2077 с будущей аркой 2078+, влияет на квест «Радио молчание» и реакции городских фракций.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 847, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-13", "author": "concept_team", "changes": "Квест «Край симуляции» адаптирован под шаблон Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

