INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-berlin-ostalgie-cafe', 'Берлин 2030-2039 — «Кафе остальгии»', 'Игрок пробует солянку, слушает истории жителей Восточного Берлина и решает, поддерживать ли остальгию.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-berlin-ostalgie-cafe", "title": "Берлин 2030-2039 — «Кафе остальгии»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["berlin", "ostalgie", "food"], "topics": ["cultural-experience", "social-history"], "related_systems": ["narrative-service", "reputation-system", "economy-system"], "related_documents": [{"id": "canon-region-europe-berlin-2030-2039", "relation": "contextualizes"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/europe/berlin/2030-2039/quest-016-ostalgie-cafe.md", "visibility": "internal", "audience": ["narrative", "quest-design", "localization"], "risk_level": "low"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Markdown-квест не раскрывал атмосферу DDR-ностальгии и выбор игрока между романтизацией и критикой прошлого.", "goal": "Структурировать посещение кафе «Ostalgie», показать традиционные блюда и диалоги с «осси».", "essence": "Игрок пробует солянку, слушает истории жителей Восточного Берлина и решает, поддерживать ли остальгию.", "key_points": ["Воссозданный интерьер ГДР с плакатами, мебелью и радио-плейлистом.", "Вариант заказа и беседы влияет на культурную репутацию.", {"Моральный выбор": "поддержать ностальгию или подчеркнуть критический взгляд."}]}, "content": {"sections": [{"id": "arrival", "title": "Прибытие в кафе", "body": "Игрок получает подсказку о скрытой двери и попадает в пространство с DDR-витринами.\\nОчередь из пожилых «осси» формирует первый социальный контакт.\\n", "mechanics_links": [], "assets": []}, {"id": "tasting", "title": "Дегустация меню", "body": "- Заказ солянки, DDR-пиццы и Club Cola с вариантами подачи.\\n- Мини-игра по подбору идеального сочетания ингредиентов.\\n- Сбор фактов об истории Ostalgie и Герте Хойвер.\\n", "mechanics_links": [], "assets": []}, {"id": "conversations", "title": "Разговоры и выбор", "body": "Пожилые посетители делятся воспоминаниями: очереди, Trabi, цитата «Es war nicht alles schlecht».\\nИгрок выбирает: поддержать ностальгию (+репутация ostalgie) или подчеркнуть проблемы прошлого (+репутация реформаторов).\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- 800 XP, -20 кредов и +10 к параметру «Культура».\\n- Ачивка «Ostalgie Insight» и открытие дальнейших DDR-квестов.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [{"term": "Ostalgie", "definition": "Ностальгия жителей Восточной Германии по элементам жизни в ГДР."}], "references": [], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-12", "author": "concept_director", "changes": "Конвертация квеста «Кафе остальгии» в knowledge-entry."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIME