INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-moscow-2077-npc-detective', 'Москва 2061-2077 — Детектив NPC', 'Игрок собирает улики по городу, определяет виновника и решает — арестовать, шантажировать или совершить самосуд.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-moscow-2077-npc-detective", "title": "Москва 2061-2077 — Детектив NPC", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-13T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["moscow", "investigation", "justice"], "topics": ["detective", "pvp"], "related_systems": ["narrative-service", "investigation-service", "pvp-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/cis/moscow/2061-2077/quest-039-npc-detective.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Нужно расследование, связывающее убийства NPC с возможным участием реального игрока.", "goal": "Провести детективный квест, ведущий к конфронтации с игроком-преступником и выбору исхода.", "essence": "Игрок собирает улики по городу, определяет виновника и решает — арестовать, шантажировать или совершить самосуд.", "key_points": ["Места преступлений требуют анализа и сопоставления цифровых следов.", "Подозреваемый может оказаться действующим игроком, что запускает PvP.", "Выбор влияет на репутацию и статус «Разыскиваемый»."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Квест предлагает сеть расследований по убийствам NPC. Нужно собрать улики, допросить свидетелей и выйти на след\\nпредполагаемого игрока-убийцы.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Осмотреть пять мест преступлений, используя сканеры и проверки расследования (DC 18).\\n2. Сопоставить улики в хранилище полиции и выявить цифровой профиль подозреваемого.\\n3. Допросить NPC и игроков-свидетелей, собрать дополнительные зацепки.\\n4. Встретиться с подозреваемым игроком на выбранной локации и провести конфронтацию.\\n5. Принять решение: арестовать, шантажировать или ликвидировать преступника.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Преступления происходят в деловых блоках архологии; полиция перенасыщена делами, поэтому детективы используют внешних агентов.\\nКамеры, биометрия и дроны создают богатую доказательную базу.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- 2 000 XP, 10 000 еддис, репутация «Детективы» +5.\\n- Если преступник — игрок, он получает статус «Разыскиваемый».\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "Анализ мест преступления, сопоставление улик, допросы с ветвлением, PvP-конфронтация, моральный выбор.\\n", "mechanics_links": [], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест связан с системой правосудия Москвы и дальнейшими миссиями по охоте за преступниками; влияет на liveops-ивенты полиции.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 849, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-13", "author": "concept_team", "changes": "Квест «Детектив NPC» адаптирован под шаблон Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

