INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-moscow-2077-last-warning', 'Москва 2061-2077 — Последнее предупреждение', 'Игроки получают зашифрованное сообщение, расшифровывают его, готовят переносимые ресурсы и переживают обратный отсчёт до перезапуска.', 'event', 1, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "receive_encrypted_signal", "text": "Принять зашифрованный сигнал", "type": "interact", "target": "encrypted_signal_broadcast", "count": 1, "optional": false}, {"id": "collect_key_fragments", "text": "Собрать фрагменты ключа по городу", "type": "collect", "target": "legacy_key_fragment", "count": 5, "optional": false}, {"id": "decrypt_message", "text": "Коллективно расшифровать сообщение", "type": "interact", "target": "decryption_puzzle", "count": 1, "optional": false}, {"id": "receive_legacy_briefing", "text": "Получить инструктаж о переносимых системах", "type": "interact", "target": "legacy_hub", "count": 1, "optional": false}, {"id": "collect_legacy_token", "text": "Собрать Legacy Token", "type": "collect", "target": "legacy_token", "count": 1, "optional": false}, {"id": "prepare_resources", "text": "Упаковать ресурсы и завершить контракты", "type": "interact", "target": "resource_packing", "count": 1, "optional": false}, {"id": "wait_countdown", "text": "Следить за семидневным таймером", "type": "wait", "target": "season_countdown", "count": 7, "optional": false}]'::jsonb, '{"experience": 15000, "money": 0, "items": [{"item_id": "legacy_token", "quantity": 1}], "reputation": {}, "unlocks": ["legacy_rewards", "season_reset_access"]}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-moscow-2077-last-warning", "title": "Москва 2061-2077 — Последнее предупреждение", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-13T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["moscow", "season-reset", "legacy"], "topics": ["meta-event", "liveops"], "related_systems": ["narrative-service", "liveops", "economy-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/cis/moscow/2061-2077/quest-040-last-warning.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "high"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Требовался мета-квест, завершающий период 2061-2077 и подготавливающий игроков к перезапуску мира.", "goal": "Передать предупреждение о завершении лиги, объяснить наследуемые системы и дать время на подготовку.", "essence": "Игроки получают зашифрованное сообщение, расшифровывают его, готовят переносимые ресурсы и переживают обратный отсчёт до перезапуска.", "key_points": ["Городской ивент запускает волну аномалий и совместной расшифровки.", "Игроки собирают Legacy Token и формируют списки переносимых предметов.", "Обратный отсчёт на семь дней сопровождается уникальными активностями и скидками."]}, "quest_definition": {"quest_type": "event", "level_min": 1, "level_max": null, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "receive_encrypted_signal", "text": "Принять зашифрованный сигнал", "type": "interact", "target": "encrypted_signal_broadcast", "count": 1, "optional": false}, {"id": "collect_key_fragments", "text": "Собрать фрагменты ключа по городу", "type": "collect", "target": "legacy_key_fragment", "count": 5, "optional": false}, {"id": "decrypt_message", "text": "Коллективно расшифровать сообщение", "type": "interact", "target": "decryption_puzzle", "count": 1, "optional": false}, {"id": "receive_legacy_briefing", "text": "Получить инструктаж о переносимых системах", "type": "interact", "target": "legacy_hub", "count": 1, "optional": false}, {"id": "collect_legacy_token", "text": "Собрать Legacy Token", "type": "collect", "target": "legacy_token", "count": 1, "optional": false}, {"id": "prepare_resources", "text": "Упаковать ресурсы и завершить контракты", "type": "interact", "target": "resource_packing", "count": 1, "optional": false}, {"id": "wait_countdown", "text": "Следить за семидневным таймером", "type": "wait", "target": "season_countdown", "count": 7, "optional": false}], "rewards": {"experience": 15000, "money": 0, "items": [{"item_id": "legacy_token", "quantity": 1}], "reputation": {}, "unlocks": ["legacy_rewards", "season_reset_access"]}, "branches": []}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Квест закрывает период 2061-2077. Все игроки получают синхронное сообщение, запускается городской мета-ивент и начинается подготовка\\nк перезапуску лиги.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Принять зашифрованный сигнал и собрать фрагменты ключа по городу.\\n2. Коллективно расшифровать сообщение, используя пазлы и временные окна.\\n3. Получить инструктаж о переносимых системах: артефакты, титулы, косметика.\\n4. Выполнить задачи подготовки: собрать Legacy Token, упаковать ресурсы, завершить незакрытые контракты.\\n5. Следить за семидневным таймером и участвовать в финальных активностях до перезапуска.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Сообщение распространяется через сетевые экраны, радиостанции и дроны. Центр управления лиги координирует ивенты, а в деловых\\nрайонах открываются временные хабы Legacy.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- 15 000 XP, Legacy Token для следующей лиги.\\n- Фиксация переносимых достижений и косметики в профиле игрока.\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "Городской broadcast, кооперативная расшифровка, подготовка к сезону, ограниченное время, управление наследием.\\n", "mechanics_links": [], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест завершает период 2061-2077 и открывает арку 2078-2093; связан с глобальными liveops и системой Legacy Rewards.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 850, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-13", "author": "concept_team", "changes": "Квест «Последнее предупреждение» адаптирован под шаблон Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

