INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-berlin-checkpoint-charlie', 'Берлин 2030-2039 — «Чекпоинт Чарли»', 'Игрок исследует музей, участвует в VR-симуляции 1961 года и пробует инсценировку побега через КПП.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-berlin-checkpoint-charlie", "title": "Берлин 2030-2039 — «Чекпоинт Чарли»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["berlin", "cold-war", "checkpoint-charlie"], "topics": ["historical-exploration", "vr-experience"], "related_systems": ["narrative-service", "history-system", "reputation-system"], "related_documents": [{"id": "canon-region-europe-berlin-2030-2039", "relation": "contextualizes"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/europe/berlin/2030-2039/quest-020-checkpoint-charlie.md", "visibility": "internal", "audience": ["narrative", "quest-design", "education"], "risk_level": "medium"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Описание Checkpoint Charlie не фиксировало интерактивы музея и VR-реконструкции танкового противостояния.", "goal": "Структурировать визит к КПП с фокусом на истории побегов и холодной войне.", "essence": "Игрок исследует музей, участвует в VR-симуляции 1961 года и пробует инсценировку побега через КПП.", "key_points": ["Реплика будки и актёры в форме союзников.", "VR «танкового противостояния» и музейный маршрут.", "Связь с берлинской стеной и историей холодной войны."]}, "content": {"sections": [{"id": "museum_tour", "title": "Экскурсия по музею", "body": "Игрок стартует у таблички «You are leaving the American Sector» и проходит экспозицию побегов.\\nИнтерфейс показывает реальные артефакты и маршруты перебежчиков.\\n", "mechanics_links": [], "assets": []}, {"id": "vr_sim", "title": "VR-симуляция", "body": "- Реконструкция танкового противостояния октября 1961.\\n- Игрок управляет переговорами между США и СССР.\\n", "mechanics_links": [], "assets": []}, {"id": "escape_trial", "title": "Реконструкция побега", "body": "Интерактивная инсценировка с актёрами позволяет попробовать пройти КПП,\\nчто влияет на параметр «Хладнокровие».\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- 1 500 XP, -10 кредов и +10 к параметру «Культура».\\n- Ачивка «Пересёк Чарли» и запись в дневник холодной войны.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [{"term": "Checkpoint Charlie", "definition": "Главный контрольный пункт между американским и советским секторами Берлина в 1961–1990 годах, символ холодной войны."}], "references": [], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-12", "author": "concept_director", "changes": "Конвертация квеста «Чекпоинт Чарли» в knowledge-entry и описание VR-сценариев."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

