INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-berlin-tempelhof-airport', 'Берлин 2040-2060 — «Темпельхоф аэропорт»', 'Игрок исследует поле, участвует в кайтинге и находит подпольный рейв в ангарах.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-berlin-tempelhof-airport", "title": "Берлин 2040-2060 — «Темпельхоф аэропорт»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["berlin", "tempelhof", "park"], "topics": ["urban-reuse", "leisure"], "related_systems": ["narrative-service", "mood-system", "exploration-system"], "related_documents": [{"id": "canon-region-europe-berlin-2020-2093", "relation": "contextualizes"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/europe/berlin/2040-2060/quest-023-flughafen-tempelhof.md", "visibility": "internal", "audience": ["narrative", "quest-design", "tourism"], "risk_level": "low"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Markdown не раскрывал потенциал Tempelhof как гибридного парка, исторического музея и места рейвов.", "goal": "Структурировать прогулку по взлётным полосам с активностями и историческими акцентами.", "essence": "Игрок исследует поле, участвует в кайтинге и находит подпольный рейв в ангарах.", "key_points": ["Показываем наследие воздушного моста и нацистской архитектуры.", "Отображаем гигантские открытые пространства и ветровые активности (кайт, вело, пикник).", "Упоминаем ночные рейвы в ангарах и расслабляющий эффект."]}, "content": {"sections": [{"id": "field", "title": "Поле и активности", "body": "Игрок приходит на взлётные полосы, где доступны кайтсерфинг, вело-дорожки и пикники.\\nГлобальный ветерной эффект снижает стресс и повышает настроение.\\n", "mechanics_links": [], "assets": []}, {"id": "hangars", "title": "Ангары и рейвы", "body": "Заброшенные терминалы открывают путь в подпольные вечеринки и культурные события.\\nИгрок принимает решение, участвовать ли в рейве или остаться на семейных активностях.\\n", "mechanics_links": [], "assets": []}, {"id": "heritage", "title": "Исторический слой", "body": "VR-экспозиция рассказывает о Berlin Airlift и нацистском происхождении комплекса.\\nВыбор экспонатов для публикации влияет на репутацию исторической ветки.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- 1 000 XP, антистресс «Tempelhof Breeze» (−40% стресс), ачивка «Пилот Темпельхофа».\\n- Дополнительно: +5 к репутации туристической линии.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [{"term": "Berlin Airlift", "definition": "Операция 1948–1949 годов по доставке грузов в Западный Берлин, где Tempelhof был ключевым аэропортом."}], "references": [], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-12", "author": "concept_director", "changes": "Конвертация квеста «Темпельхоф аэропорт» в knowledge-entry и добавление исторической VR-экспозиции."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

