INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-berlin-stasi-archives', 'Берлин 2020-2029 — «Архивы Штази»', 'Игрок взламывает досье знакомого, сталкивается с предательством и решает, кого защитить.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-berlin-stasi-archives", "title": "Берлин 2020-2029 — «Архивы Штази»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["berlin", "stasi", "espionage"], "topics": ["infiltration", "moral-dilemma"], "related_systems": ["narrative-service", "hacking-system", "reputation-system"], "related_documents": [{"id": "canon-region-europe-berlin-2020-2093", "relation": "contextualizes"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/europe/berlin/2020-2029/quest-008-stasi-archives.md", "visibility": "internal", "audience": ["narrative", "quest-design", "history"], "risk_level": "high"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Markdown не отражал механику проникновения и тяжесть выбора при вскрытии архивов Штази.", "goal": "Оформить квест как хакерско-историческую миссию с эмоциональными последствиями.", "essence": "Игрок взламывает досье знакомого, сталкивается с предательством и решает, кого защитить.", "key_points": ["Индустриальное здание Stasi Museum и километры папок.", "DC 20+ на взлом и доступ к микрофильмам.", "Три исхода, влияющие на репутацию и моральный фактор."]}, "content": {"sections": [{"id": "infiltration", "title": "Проникновение", "body": "Игрок маскируется под исследователя, проходит охрану и подключается к архивной сети.\\nВзлом совмещает ретро-терминалы и современный софт.\\n", "mechanics_links": [], "assets": []}, {"id": "discovery", "title": "Раскрытие правды", "body": "- Досье показывает, что друзья героя служили информаторами.\\n- Мини-игра по сборке разорванных документов открывает дополнительные факты.\\n- Психологический стресс отражается на параметре «Стабильность».\\n", "mechanics_links": [], "assets": []}, {"id": "choice", "title": "Решение", "body": "- Публикация: +15 к репутации реформаторов, но враги начинают охоту.\\n- Сокрытие: сохраняет отношения, даёт внутренний конфликт.\\n- Уничтожение: стирает следы, но активисты теряют доверие.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- 4 000 XP и ресурс «Архивные данные».\\n- Репутация линий истории ±15 в зависимости от выбора.\\n- Доступ к цепочке расследований прошлого.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [{"term": "Stasi", "definition": "Министерство госбезопасности ГДР с сетью информаторов и масштабными архивами."}], "references": [], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-12", "author": "concept_director", "changes": "Перенос квеста «Архивы Штази» в knowledge-entry и детализация морального решения."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

