INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-berlin-club-mate-empire', 'Берлин 2040-2060 — «Империя Club-Mate»', 'Игрок получает права на Club-Mate, строит сеть поставок и превращается в «Baron» сцены.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-berlin-club-mate-empire", "title": "Берлин 2040-2060 — «Империя Club-Mate»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["berlin", "club-mate", "business"], "topics": ["beverage-economy", "branding"], "related_systems": ["narrative-service", "economy-system", "reputation-system"], "related_documents": [{"id": "canon-region-europe-berlin-2020-2093", "relation": "contextualizes"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/europe/berlin/2040-2060/quest-025-club-mate-empire.md", "visibility": "internal", "audience": ["narrative", "quest-design", "economy"], "risk_level": "medium"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Markdown не описывал экономическую экспансию Club-Mate и влияние на техно-сцену.", "goal": "Создать бизнес-квест о дистрибуции культового напитка и управлении брендом.", "essence": "Игрок получает права на Club-Mate, строит сеть поставок и превращается в «Baron» сцены.", "key_points": ["Переговоры с производителем и технический запуск складов.", "Маркетинговые кампании для рейверов, хакеров и сетевых фестивалей.", "Экспансия в Европу и усиление бренда как символа культуры."]}, "content": {"sections": [{"id": "acquisition", "title": "Приобретение прав", "body": "Игрок покупает дистрибьюторские права и анализирует логистику поставок по Берлину.\\nУстанавливаются KPI продаж и прогнозы спроса.\\n", "mechanics_links": [], "assets": []}, {"id": "marketing", "title": "Маркетинг и культ", "body": "Запускается кампания «Hacker''s Fuel», спонсируются рейвы и хакатоны.\\nИгрок решает, продвигать ли новые вкус-лимиты или сохранять классический рецепт.\\n", "mechanics_links": [], "assets": []}, {"id": "expansion", "title": "Расширение империи", "body": "Поставки выходят за пределы Берлина, появляются филиалы в техно-столицах Европы.\\nУправление ценой и эксклюзивными договорами влияет на прибыль и репутацию.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- 3 000 XP, пассивный доход 30 000 кредов в неделю, титул «Club-Mate Baron».\\n- Репутация экономической ветки +15 и доступ к коллаборациям с брендами напитков.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [{"term": "Club-Mate", "definition": "Немецкий безалкогольный напиток на основе мате, культовый для хакеров и техно-сцены Берлина."}], "references": [], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-12", "author": "concept_director", "changes": "Конвертация квеста «Империя Club-Mate» в knowledge-entry и описание бизнес-экспансии."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

