INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-moscow-2078-2093-meta-court', 'Москва 2078-2093 — Мета-суд', 'Игрок попадает на слушание, аргументирует позицию и получает вердикт от случайно выбранных присяжных.', 'side', 20, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "receive_case_assignment", "text": "Получить назначение дела", "type": "interact", "target": "court_system", "count": 1, "optional": false}, {"id": "study_evidence", "text": "Изучить материалы обвинения", "type": "interact", "target": "case_evidence", "count": 1, "optional": false}, {"id": "participate_hearing", "text": "Принять участие в слушании", "type": "interact", "target": "court_hearing", "count": 1, "optional": false}, {"id": "vote_verdict", "text": "Проголосовать за вердикт", "type": "interact", "target": "verdict_voting", "count": 1, "optional": false}]'::jsonb, '{"experience": 500, "currency": 2000, "items": [], "reputation": {"justice": 1}}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-moscow-2078-2093-meta-court", "title": "Москва 2078-2093 — Мета-суд", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-13T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["moscow", "justice", "community"], "topics": ["player_governance", "social_accountability"], "related_systems": ["narrative-service", "moderation-service", "reputation-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/cis/moscow/2078-2093/quest-047-meta-court.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Требовалось социальное правосудие, управляемое игроками, для регулирования поведения в конце лиги.", "goal": "Создать ролевой судебный процесс, где присяжные-игроки решают судьбу обвиняемых.", "essence": "Игрок попадает на слушание, аргументирует позицию и получает вердикт от случайно выбранных присяжных.", "key_points": ["Присяжные — реальные игроки, что усиливает социальную ответственность.", "Наказания влияют на аккаунт и экономику.", "Участники суда получают награды и репутацию."]}, "quest_definition": {"quest_type": "side", "level_min": 20, "level_max": null, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "receive_case_assignment", "text": "Получить назначение дела", "type": "interact", "target": "court_system", "count": 1, "optional": false}, {"id": "study_evidence", "text": "Изучить материалы обвинения", "type": "interact", "target": "case_evidence", "count": 1, "optional": false}, {"id": "participate_hearing", "text": "Принять участие в слушании", "type": "interact", "target": "court_hearing", "count": 1, "optional": false}, {"id": "vote_verdict", "text": "Проголосовать за вердикт", "type": "interact", "target": "verdict_voting", "count": 1, "optional": false}], "rewards": {"experience": 500, "currency": 2000, "items": [], "reputation": {"justice": 1}}, "branches": []}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Квест переносит игрока в залы Мета-суда, где сообщество регулирует поведение участников лиги. Игрок может выступать в роли\\nобвиняемого, защитника или присяжного.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Получить назначение дела и изучить материалы обвинения.\\n2. Провести слушание: представить доказательства, вызвать свидетелей, ответить на вопросы суда.\\n3. Проголосовать (или дождаться вердикта) и зафиксировать решение.\\n4. Применить наказание или объявить оправдание, уведомив участников.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Мета-суд проходит в куполе административного центра Москвы. Зал оформлен голографическими экранами с логами, судебными аватарами\\nи интерфейсами репутации. NetWatch мониторит процесс, но не вмешивается.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- Присяжные: 500 XP, 2 000 еддис, репутация «Правосудие» +1.\\n- Обвиняемый (оправданный): восстановление репутации и публичное уведомление.\\n- Обвиняемый (виновный): наказание согласно вердикту (бан, конфискация, штрафы).\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "Ролевая судебная система, голосование игроков, интеграция модерации и применение реальных в игре наказаний.\\n", "mechanics_links": [], "assets": []}, {"id": "connections", "title": "Связи", "body": "Связан с системой правосудия сообщества, мероприятиями liveops и квестами, отслеживающими нарушения и репутацию.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 857, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-13", "author": "concept_team", "changes": "Квест «Мета-суд» адаптирован под формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

