INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-moscow-2093-world-boss', 'Москва 2078-2093 — Мировой босс', 'Сервер готовится к вторжению, сражается в нескольких фазах и распределяет награды по рейтингу вклада.', 'event', 30, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "evacuate_npcs", "text": "Помочь в эвакуации NPC из зоны вторжения", "type": "interact", "target": "evacuation_zone", "count": 1, "optional": false}, {"id": "fight_physical_phase", "text": "Сражаться в физической фазе боя", "type": "combat", "target": "world_boss_physical", "count": 1, "optional": false}, {"id": "hack_shields", "text": "Взломать щиты в хакерской фазе", "type": "interact", "target": "boss_shields", "count": 1, "optional": false}, {"id": "survive_berserk_phase", "text": "Выжить в финальной берсерк-фазе (60 минут)", "type": "combat", "target": "world_boss_berserk", "count": 1, "optional": false}]'::jsonb, '{"experience": 10000, "currency": 10000, "items": [], "reputation": {}}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-moscow-2093-world-boss", "title": "Москва 2078-2093 — Мировой босс", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-13T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["moscow", "raid", "world-event"], "topics": ["large-scale-combat", "liveops-event"], "related_systems": ["narrative-service", "combat-service", "liveops-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/cis/moscow/2078-2093/quest-048-world-boss.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "extreme"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Финальному сезону Москвы требовалось масштабное рейдовое событие для всего сервера.", "goal": "Организовать бой с гигантским роуг-ИИ, объединяющий игроков разных специализаций.", "essence": "Сервер готовится к вторжению, сражается в нескольких фазах и распределяет награды по рейтингу вклада.", "key_points": ["Вторжение роуг-ИИ запускает обязательную эвакуацию NPC и координацию игроков.", "Бой разделён на физическую, хакерскую и берсерк-фазы.", "Награды зависят от суммарного урона и роли участника."]}, "quest_definition": {"quest_type": "event", "level_min": 30, "level_max": null, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "evacuate_npcs", "text": "Помочь в эвакуации NPC из зоны вторжения", "type": "interact", "target": "evacuation_zone", "count": 1, "optional": false}, {"id": "fight_physical_phase", "text": "Сражаться в физической фазе боя", "type": "combat", "target": "world_boss_physical", "count": 1, "optional": false}, {"id": "hack_shields", "text": "Взломать щиты в хакерской фазе", "type": "interact", "target": "boss_shields", "count": 1, "optional": false}, {"id": "survive_berserk_phase", "text": "Выжить в финальной берсерк-фазе (60 минут)", "type": "combat", "target": "world_boss_berserk", "count": 1, "optional": false}], "rewards": {"experience": 10000, "currency": 10000, "items": [], "reputation": {}}, "branches": []}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Событие запускается по расписанию. В центре Москвы появляется мировой босс, требующий участия десятков команд и слаженной\\nподдержки нетраннеров.\\n", "mechanics_links": [], "assets": []}, {"id": "phases", "title": "Этапы боя", "body": "1. Вторжение и эвакуация городских районов.\\n2. Фаза физического боя против корпуса ИИ.\\n3. Хакерская фаза с отключением щитов и подрежимов.\\n4. Финальная берсерк-фаза с ограничением по времени в 60 минут.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Вторжение происходит на площади перед Архологией: голографические барьеры, эвакуационные коридоры и буря цифрового шума.\\nNetWatch проецирует тактические сетки, а мегакорпы предоставляют артиллерию и медицинские платформы.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- Топ-1 по урону: легендарное оружие и 500 000 еддис.\\n- Топ-10: редкие импланты и 100 000 еддис.\\n- Топ-100: редкое оружие и 50 000 еддис.\\n- Все участники: 10 000 XP и 10 000 еддис.\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "Мировое событие всего сервера, рейдовый контент, рейтинговое распределение наград и ограничение по времени.\\n", "mechanics_links": [], "assets": []}, {"id": "connections", "title": "Связи", "body": "Weekly-event Москвы, влияющий на стенды liveops и готовящий игроков к финальным миссиям лиги.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 858, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-13", "author": "concept_team", "changes": "Квест «Мировой босс» адаптирован под шаблон Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

