INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-berlin-dj-career', 'Берлин 2040-2060 — «Карьера DJ»', 'Игрок осваивает техники микса, записывает демо и становится резидентом Berghain/Tresor.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-berlin-dj-career", "title": "Берлин 2040-2060 — «Карьера DJ»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["berlin", "techno", "career"], "topics": ["music", "creative-profession"], "related_systems": ["narrative-service", "talent-system", "reputation-system"], "related_documents": [{"id": "canon-region-europe-berlin-2040-2060", "relation": "contextualizes"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/europe/berlin/2040-2060/quest-027-dj-career.md", "visibility": "internal", "audience": ["narrative", "quest-design", "audio"], "risk_level": "medium"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Markdown-квест не описывал путь от новичка до резидента культовых клубов Берлина.", "goal": "Структурировать карьеру техно-DJ с обучением, букингами и закреплением в сцене.", "essence": "Игрок осваивает техники микса, записывает демо и становится резидентом Berghain/Tresor.", "key_points": ["Тренировки на Technics 1210, CDJ и Ableton.", "Демо-микс, переговоры с промоутерами, управление сетами 8+ часов.", "Рост репутации и доход от выступлений."]}, "content": {"sections": [{"id": "training", "title": "Обучение диджеингу", "body": "Игрок проходит курсы винила и цифровых сетов, осваивая beatmatching и creative mixing.\\nНаставники из Ostgut Ton дают задания на плавные переходы и построение драматургии.\\n", "mechanics_links": [], "assets": []}, {"id": "networking", "title": "Сеть контактов и букинги", "body": "Первые выступления проходят в маленьких клубах; игрок записывает демо и шлёт его промоутерам Tresor и Watergate.\\nДиалоговые ветки определяют, кто станет агентом игрока.\\n", "mechanics_links": [], "assets": []}, {"id": "residency", "title": "Резиденство", "body": "Букинг в Berghain требует успешного сета длиной 8 часов и строгого фейс-контроля.\\nИгрок решает, развивать ли собственный лейбл или сосредоточиться на клубе.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- 8 000 XP, доход 50 000–500 000 кредов в месяц.\\n- Титул «Техно-легенда» и +50 к параметру «Фейм».\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [{"term": "Resident DJ", "definition": "Постоянный диджей клуба, играющий регулярные сеты и формирующий музыкальную политику площадки."}], "references": [], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-12", "author": "concept_director", "changes": "Конвертация квеста «Карьера DJ» в knowledge-entry."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

