INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-moscow-2093-farewell-party', 'Москва 2078-2093 — Прощальная вечеринка', 'Игрок арендует площадку, приглашает гостей, проводит вечер и делает последний обзор Москвы.', 'event', 1, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "rent_venue", "text": "Арендовать место для вечеринки", "type": "interact", "target": "party_venue_rental", "count": 1, "optional": false}, {"id": "send_invitations", "text": "Разослать приглашения друзьям и NPC", "type": "interact", "target": "invitation_system", "count": 1, "optional": false}, {"id": "host_party", "text": "Провести вечеринку с музыкой и танцами", "type": "interact", "target": "party_event", "count": 1, "optional": false}, {"id": "farewell_dialogues", "text": "Провести прощальные диалоги с близкими персонажами", "type": "interact", "target": "npc_farewell", "count": 3, "optional": false}, {"id": "final_city_review", "text": "Сделать последний обзор города", "type": "interact", "target": "city_panorama", "count": 1, "optional": false}]'::jsonb, '{"experience": 1000, "currency": -10000, "items": [], "reputation": {}}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-moscow-2093-farewell-party", "title": "Москва 2078-2093 — Прощальная вечеринка", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-13T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["moscow", "social", "finale"], "topics": ["emotional-closure", "community-bonding"], "related_systems": ["narrative-service", "social-service", "legacy-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/cis/moscow/2078-2093/quest-049-farewell-party.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Игрокам нужен эмоциональный финал сезона, завершающий отношения с NPC и сообществом.", "goal": "Организовать прощальную вечеринку, собрать друзей и зафиксировать итоговые воспоминания перед перезапуском лиги.", "essence": "Игрок арендует площадку, приглашает гостей, проводит вечер и делает последний обзор Москвы.", "key_points": ["Сцена подводит финальные диалоги с ключевыми NPC.", "Событие поддерживает социальный RP и объединяет команду.", "Квест снимает напряжение перед глобальным перерождением."]}, "quest_definition": {"quest_type": "event", "level_min": 1, "level_max": null, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "rent_venue", "text": "Арендовать место для вечеринки", "type": "interact", "target": "party_venue_rental", "count": 1, "optional": false}, {"id": "send_invitations", "text": "Разослать приглашения друзьям и NPC", "type": "interact", "target": "invitation_system", "count": 1, "optional": false}, {"id": "host_party", "text": "Провести вечеринку с музыкой и танцами", "type": "interact", "target": "party_event", "count": 1, "optional": false}, {"id": "farewell_dialogues", "text": "Провести прощальные диалоги с близкими персонажами", "type": "interact", "target": "npc_farewell", "count": 3, "optional": false}, {"id": "final_city_review", "text": "Сделать последний обзор города", "type": "interact", "target": "city_panorama", "count": 1, "optional": false}], "rewards": {"experience": 1000, "currency": -10000, "items": [], "reputation": {}}, "branches": []}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Квест фокусируется на эмоциональном прощании с миром Москвы перед перезапуском. Игроку нужно организовать атмосферную вечеринку\\nи завершить активные сюжетные линии.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Арендовать место и оформить его в стиле финала лиги.\\n2. Разослать приглашения друзьям, NPC и союзным фракциям.\\n3. Провести вечеринку: музыка, танцы, обмен историями и подарок каждому гостю.\\n4. Прощальные диалоги с близкими персонажами.\\n5. Последний обзор города перед окончанием сезона.\\n", "mechanics_links": [], "assets": []}, {"id": "emotional_content", "title": "Эмоциональный контент", "body": "Диалоги и воспоминания подчёркивают совместные приключения. Игрок фиксирует обещания встретиться в следующей лиге и получает\\nатмосферную сцену заката над Москвой.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Вечеринка проходит в панорамном куполе башни архологии с видами на неоновые районы. Музыкальные станции управляют сетевыми\\nплейлистами, а вспомогательные дроны обслуживают гостей.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- Опыт: 1 000 XP.\\n- Расходы: 10 000 еддис на организацию.\\n- Ачивка «До встречи в следующей жизни» и эмоция «Катарсис».\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "Социальное событие, ролевые сцены, атмосферное завершение и подготовка к legacy-механикам.\\n", "mechanics_links": [], "assets": []}, {"id": "connections", "title": "Связи", "body": "Финальный social-контент перед квестом «Новое начало», связывает игроков с NPC и подводит итоги сезона.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 859, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-13", "author": "concept_team", "changes": "Квест «Прощальная вечеринка» адаптирован под шаблон Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

