INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-moscow-2078-2093-new-beginning', 'Москва 2078-2093 — Новое начало', 'Игрок выбирает, что переносит в новую лигу, завершает личный квест и наблюдает кат-сцену заката над Москвой.', 'main', 1, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "select_legacy", "text": "Выбрать legacy-набор для переноса", "type": "interact", "target": "legacy_selection", "count": 1, "optional": false}, {"id": "record_message", "text": "Записать послание себе в будущем", "type": "interact", "target": "message_recording", "count": 1, "optional": false}, {"id": "complete_final_quest", "text": "Выполнить финальный квест", "type": "interact", "target": "final_quest_variant", "count": 1, "optional": false}, {"id": "countdown_sequence", "text": "Пережить 10-секундный обратный отсчёт", "type": "interact", "target": "countdown_timer", "count": 1, "optional": false}, {"id": "watch_cutscene", "text": "Принять кат-сцену перерождения", "type": "interact", "target": "rebirth_cutscene", "count": 1, "optional": false}]'::jsonb, '{"experience": 0, "currency": 0, "items": [], "reputation": {}}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-moscow-2078-2093-new-beginning", "title": "Москва 2078-2093 — Новое начало", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-13T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["moscow", "finale", "legacy"], "topics": ["league_transition", "player_legacy"], "related_systems": ["narrative-service", "legacy-service", "progression-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/cis/moscow/2078-2093/quest-050-new-beginning.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Необходим финальный квест Москвы, закрывающий лигу и настраивающий legacy на новый сезон.", "goal": "Позволить игроку выбрать наследие, оставить послание и пройти финальную сцену перерождения.", "essence": "Игрок выбирает, что переносит в новую лигу, завершает личный квест и наблюдает кат-сцену заката над Москвой.", "key_points": ["Legacy-выбор формирует стартовые бонусы следующей лиги.", "Финальный обратный отсчёт создаёт напряжение.", "Кат-сцена подводит итог 50 квестам Москвы."]}, "quest_definition": {"quest_type": "main", "level_min": 1, "level_max": null, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "select_legacy", "text": "Выбрать legacy-набор для переноса", "type": "interact", "target": "legacy_selection", "count": 1, "optional": false}, {"id": "record_message", "text": "Записать послание себе в будущем", "type": "interact", "target": "message_recording", "count": 1, "optional": false}, {"id": "complete_final_quest", "text": "Выполнить финальный квест", "type": "interact", "target": "final_quest_variant", "count": 1, "optional": false}, {"id": "countdown_sequence", "text": "Пережить 10-секундный обратный отсчёт", "type": "interact", "target": "countdown_timer", "count": 1, "optional": false}, {"id": "watch_cutscene", "text": "Принять кат-сцену перерождения", "type": "interact", "target": "rebirth_cutscene", "count": 1, "optional": false}], "rewards": {"experience": 0, "currency": 0, "items": [], "reputation": {}}, "branches": []}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Финальный квест Москвы подводит итоги лиги и переводит игрока в новый сезон. Нужно выбрать наследие, завершить личную арку и\\nпройти сцену перерождения.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Выбрать legacy-набор: один артефакт, три скина, титулы и ачивки для переноса.\\n2. Записать послание себе в будущем: текстовое или видео-завещание.\\n3. Выполнить вариативный финальный квест, основанный на достижениях игрока.\\n4. Пережить 10-секундный обратный отсчёт на крыше архологии.\\n5. Принять кат-сцену перерождения и загрузиться в новую лигу.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Сцена разворачивается на крыше центральной архологии. Панорама Москвы сменяется белым светом, голограммы выводят достижения за 50\\nквестов, а дроны проецируют благодарственные сообщения от NPC.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- Legacy: выбранные артефакты, скины, титулы, переносимые в новую лигу.\\n- Титул «Ветеран Москвы 2020-2093».\\n- Эмоциональная кат-сцена и фиксация прогресса.\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "Завершение лиги, система legacy, эмоциональное завершение, индивидуальное финальное испытание.\\n", "mechanics_links": [], "assets": []}, {"id": "connections", "title": "Связи", "body": "Завершает московскую линейку из 50 квестов, активирует старт новой лиги 2094+ и подключает игрока к глобальному прогрессу.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 860, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-13", "author": "concept_team", "changes": "Квест «Новое начало» адаптирован под шаблон Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

