INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-berlin-2030-2039-east-west-reunion', 'Берлин 2030-2039 — Воссоединение Востока и Запада', 'Игрок организует фестиваль через пятьдесят лет после падения стены и выбирает будущее культурного баланса.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-berlin-2030-2039-east-west-reunion", "title": "Берлин 2030-2039 — Воссоединение Востока и Запада", "document_type": "canon", "category": "quest", "status": "draft", "version": "0.2.0", "last_updated": "2025-12-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["berlin", "festival", "unity"], "topics": ["social-event", "political-choice"], "related_systems": ["narrative-service", "social-system", "reputation-system"], "related_documents": [{"id": "canon-region-europe-berlin-2020-2093", "relation": "contextualizes"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/europe/berlin/2030-2039/quest-013-east-west-reunion.md", "visibility": "internal", "audience": ["narrative", "live_ops", "community"], "risk_level": "medium"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Квест фестиваля «Восток встречает Запад» не учитывал социальные выборы и влияние на культуру Берлина.", "goal": "Структурировать события примирения Ost/West с ключевыми решениями и последствиями для города.", "essence": "Игрок организует фестиваль через пятьдесят лет после падения стены и выбирает будущее культурного баланса.", "key_points": ["Команда фестиваля состоит из представителей обеих сторон берлинской истории.", "Программа объединяет техно, art-хаус и наследие ГДР.", "Финальный выбор меняет параметры единства и восприятие Берлина."]}, "content": {"sections": [{"id": "preparation", "title": "Подготовка фестиваля", "body": "- Сформировать оргкомитет с равным числом Ost и West участников.\\n- Настроить площадки: техно-арена, музей истории ГДР, Ostalgie-маркет.\\n- Обеспечить логистику, безопасность и медиа-поддержку.\\n", "mechanics_links": ["mechanics/world/event-management.yaml", "mechanics/quests/quest-system.yaml"], "assets": []}, {"id": "festival_program", "title": "Программа «Восток встречает Запад»", "body": "- Западная сцена: техно, арт-инсталляции, экономические панели.\\n- Восточная сцена: социалистическое наследие, Ostalgie-продукты, Trabant vs BMW шоу.\\n- Примирительные мероприятия: круглый стол, VR-хроника падения стены, совместный финал.\\n", "mechanics_links": ["mechanics/world/culture-events.yaml", "mechanics/quests/quest-system.yaml"], "assets": []}, {"id": "decisions", "title": "Ключевые решения", "body": "- Полное единство: объединённое управление культурой и бонус к репутации «Единство».\\n- Сохранить различия: добавить перманентные Ost/West бусты к разнообразию.\\n- Новое разделение: риск конфликта, бонус к радикальным фракциям.\\n", "mechanics_links": ["mechanics/world/reputation-system.yaml", "mechanics/quests/branching-outcomes.yaml"], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "- 5 000 XP.\\n- Репутация «Единство»: +20 при гармоничном исходе.\\n- Город получает долгосрочные модификаторы событий в зависимости от выбранного пути.\\n", "mechanics_links": ["mechanics/world/reputation-system.yaml"], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"needs_task": false, "github_issue": 54, "queue_reference": ["shared/trackers/queues/concept/queued.yaml#canon-quest-berlin-2030-2039-east-west-reunion"], "blockers": []}, "history": [{"version": "0.2.0", "date": "2025-12-12", "author": "content_team", "changes": "Добавлены ссылки на ивентные и репутационные механики, обновлены версия и дата."}, {"version": "0.1.0", "date": "2025-11-12", "author": "narrative_team", "changes": "Конвертирован квест «Воссоединение Востока и Запада» в YAML с социальными выборами."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

