INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-saint-petersburg-2020-2029-hermitage-heist', 'Санкт-Петербург 2020-2029 — «Ограбление Эрмитажа»', 'Игрок собирает сведения об охране, выбирает экспонат, проникает в музей и эвакуируется по Неве, балансируя стелс и экшен.', 'side', 9, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "plan_heist", "text": "Собрать досье на охрану Эрмитажа, анализировать смены, точки доступа и лазеры, выбрать цель кражи: полотно Рембрандта, набросок Да Винчи или корону Романовых", "type": "interact", "target": "hermitage_heist_planning", "count": 1, "optional": false}, {"id": "infiltrate_winter_palace", "text": "В ночь на пятницу проникнуть в музей через сервисные коридоры, синхронизировать глушение сигнализации, отключение лазеров и нейтрализацию патрулей", "type": "perform", "target": "hermitage_heist_infiltration", "count": 1, "optional": false}, {"id": "extract_artwork", "text": "Изъять выбранный экспонат, отступить через Малахитовую залу к скрытому выходу, эвакуироваться по Неве при поддержке катера и дымовых завес", "type": "perform", "target": "hermitage_heist_extraction", "count": 1, "optional": false}, {"id": "deal_with_consequences", "text": "Получить шедевр, но попасть в международные базы розыска, открыть ветки скрытия трофея и взаимодействия с коллекционерами", "type": "interact", "target": "hermitage_heist_consequences", "count": 1, "optional": false}]'::jsonb, '{"experience": 8000, "currency": 500000, "items": [], "reputation": {"Criminal_Elite": 100}, "flags": ["hermitage_heist_achievement", "international_wanted_status", "collector_contacts_unlock"]}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-saint-petersburg-2020-2029-hermitage-heist", "title": "Санкт-Петербург 2020-2029 — «Ограбление Эрмитажа»", 