INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-berlin-startup-nation', 'Берлин 2061-2077 — «Нация стартапов»', 'Игрок запускает стартап в Kreuzberg, растит его до статуса единорога и определяет будущее техно-сцены Берлина.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-berlin-startup-nation", "title": "Берлин 2061-2077 — «Нация стартапов»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["berlin", "startup", "innovation"], "topics": ["tech-hub", "entrepreneurship"], "related_systems": ["narrative-service", "economy-system", "reputation-system"], "related_documents": [{"id": "canon-lore-europe-berlin-2061-2077", "relation": "contextualizes"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/europe/berlin/2061-2077/quest-031-startup-nation.md", "visibility": "internal", "audience": ["narrative", "quest-design", "economy"], "risk_level": "medium"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Markdown-описание не структурировало путь игрока от идеи до «юнкорна» и не фиксировало влияние на экономическую мета-линию Берлина.", "goal": "Сформировать knowledge-entry о трансформации города в европейскую нацию стартапов с фокусом на инвестиционных и репутационных шагах.", "essence": "Игрок запускает стартап в Kreuzberg, растит его до статуса единорога и определяет будущее техно-сцены Берлина.", "key_points": ["Берлин позиционируется как доступная альтернатива Силиконовой долине.", "Кооперативы, техно-культура и свободные пространства усиливают уникальный вайб города.", "Успех стартапа влияет на репутацию региона и открывает глобальные tech-хабы."]}, "content": {"sections": [{"id": "ideation", "title": "Идея и формирование команды", "body": "Игрок выбирает нишу и собирает мультикультурную команду, используя сеть «Silicon Allee».\\nРешения определяют стартовые ресурсы и доступ к наставникам.\\n", "mechanics_links": [], "assets": []}, {"id": "coworking", "title": "Ко-воркинг в Кройцберге", "body": "Стартап переезжает в индустриальное пространство, адаптированное под лаборатории и студии.\\nВечерние техно-вечеринки влияют на мораль и креативность.\\n", "mechanics_links": [], "assets": []}, {"id": "pitching", "title": "Питч европейским инвесторам", "body": "Серия встреч с венчурными фондами ЕС требует навыков убеждения и экономических симуляций.\\nИгрок балансирует между быстрым ростом и устойчивостью.\\n", "mechanics_links": [], "assets": []}, {"id": "scale", "title": "Масштабирование и выбор выхода", "body": "После серии инвестиций стартап достигает статуса «единорога» или продаётся стратегическому партнёру.\\nВыбор сценария меняет репутацию Берлина как tech-столицы.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- 15 000 XP, 100 000 000 кредов, титул «Berlin Tech Founder».\\n- При неудаче: 5 000 XP и доступ к анализу ошибок для повторного запуска.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [{"term": "Silicon Allee", "definition": "Сообщество берлинских стартапов, позиционирующее город как европейский tech-hub."}], "references": [], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-12", "author": "concept_director", "changes": "Конвертация квеста «Нация стартапов» в knowledge-entry и детализация стартап-арки."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

